CREATE TABLE quran_juzs( id INTEGER PRIMARY KEY AUTO_INCREMENT, created_at DATETIME DEFAULT CURRENT_TIMESTAMP(), updated_at DATETIME ON UPDATE CURRENT_TIMESTAMP() , juz INTEGER, surah_id INTEGER, ayah_id INTEGER, FOREIGN kEY (surah_id) REFERENCES quran_surahs(id), FOREIGN kEY (ayah_id) REFERENCES quran_ayahs(id));
