UPDATE quran_ayahs SET en_text = "\\[Remember\\] when Moses said to his family, “I have spotted a fire. I will bring you some news from there, or bring you a burning brand so that you may warm yourselves.”" WHERE id = 3165;
UPDATE quran_ayahs SET en_text = "When he came to it, he was called, “Blessed is He Who is at the fire and those who are around it. Glory be to Allah, the Lord of the worlds." WHERE id = 3166;
UPDATE quran_ayahs SET en_text = "O Moses, verily this is Me – Allah, the All-Mighty, the All-Wise." WHERE id = 3167;
UPDATE quran_ayahs SET en_text = "Throw down your staff!” But when he saw it slithering as if it were a snake, he turned and ran away without looking back. “O Moses, do not be afraid! Indeed, the messengers should have no fear in My presence," WHERE id = 3168;
