UPDATE quran_ayahs SET en_text = "It will only be a single Blast, and at once they will all be brought before Us." WHERE id = 3757;
UPDATE quran_ayahs SET en_text = "On that Day no soul will be wronged in the least, nor will you be recompensed except for what you used to do." WHERE id = 3758;
