UPDATE quran_ayahs SET en_text = "They will have therein fruits and all what they ask for." WHERE id = 3761;
UPDATE quran_ayahs SET en_text = "“Peace”: a word from a Most Merciful Lord." WHERE id = 3762;
UPDATE quran_ayahs SET en_text = "“Stand apart \\[from the believers\\] this Day, O wicked ones!" WHERE id = 3763;
UPDATE quran_ayahs SET en_text = "Did I not command you, O children of Adam, not to worship Satan, for he is your sworn enemy," WHERE id = 3764;
