UPDATE quran_ayahs SET en_text = "He led great numbers of you astray. Did you not then have sense?" WHERE id = 3766;
UPDATE quran_ayahs SET en_text = "This is the Hell that you were promised." WHERE id = 3767;
UPDATE quran_ayahs SET en_text = "Burn in it Today for your disbelief.”" WHERE id = 3768;
UPDATE quran_ayahs SET en_text = "Today We will seal up their mouths, and their hands will speak to Us, and their feet will testify to all what they used to do." WHERE id = 3769;
UPDATE quran_ayahs SET en_text = "If We willed, We could have taken away their sight, so they would rush to find their way, but then how could they see?" WHERE id = 3770;
UPDATE quran_ayahs SET en_text = "And if We willed, We could have paralyzed them in their places, so they could neither move forward nor backward." WHERE id = 3771;
UPDATE quran_ayahs SET en_text = "Whomsoever We give a long life, We cause his strength to decline. Do they not then understand?" WHERE id = 3772;
UPDATE quran_ayahs SET en_text = "We have not taught him poetry, nor is it fitting for him. This is only a Reminder and a clear Qur’an" WHERE id = 3773;
UPDATE quran_ayahs SET en_text = "so that he may warn those who are truly alive and establish the argument against the disbelievers." WHERE id = 3774;
UPDATE quran_ayahs SET en_text = "Do they not see that We have created for them livestock made by Our Hands, and then they are their owners?" WHERE id = 3775;
