UPDATE quran_ayahs SET en_text = "Yet they have taken other gods besides Allah, hoping that they may be helped." WHERE id = 3778;
UPDATE quran_ayahs SET en_text = "They cannot help them, yet they serve them as their dedicated guards." WHERE id = 3779;
UPDATE quran_ayahs SET en_text = "Do not let their words grieve you. Surely We know what they conceal and what they reveal." WHERE id = 3780;
UPDATE quran_ayahs SET en_text = "Does man not see that We have created him from a sperm drop, then he becomes a clear adversary," WHERE id = 3781;
