UPDATE quran_ayahs SET en_text = "Is not the One Who created the heavens and earth able to create the like of these \\[people\\]? Yes indeed, for He is the Creator of all, the All-Knowing." WHERE id = 3785;
UPDATE quran_ayahs SET en_text = "Whenever He wills something to be, He only says to it, “Be”, and it is." WHERE id = 3786;
UPDATE quran_ayahs SET en_text = "So glory be to the One in Whose Hand is the dominion of everything, and to Him you will all be returned." WHERE id = 3787;
