UPDATE quran_ayahs SET en_text = "\\[They will be told\\], “This is the Day of Decision that you used to deny.”" WHERE id = 3808;
UPDATE quran_ayahs SET en_text = "\\[The angels will be told\\], “Gather all those who did wrong and their fellows, and whatever they used to worship" WHERE id = 3809;
