UPDATE quran_ayahs SET en_text = "When it descends on their courtyard, how terrible will be the morning of those who were warned!" WHERE id = 3964;
UPDATE quran_ayahs SET en_text = "And turn away from them for a while." WHERE id = 3965;
UPDATE quran_ayahs SET en_text = "Wait and see; soon they will see \\[their end\\]." WHERE id = 3966;
UPDATE quran_ayahs SET en_text = "Glory be to your Lord, the Lord of Might, far above what they ascribe \\[to Him\\]," WHERE id = 3967;
