UPDATE quran_ayahs SET en_text = "Does he have knowledge of the unseen, so he has insight?" WHERE id = 4818;
UPDATE quran_ayahs SET en_text = "Or has he not been informed of what is in the Scriptures of Moses," WHERE id = 4819;
