UPDATE quran_ayahs SET en_text = "That no bearer of burden will bear the burden of another," WHERE id = 4821;
UPDATE quran_ayahs SET en_text = "and that man will not receive except what he strives for," WHERE id = 4822;
UPDATE quran_ayahs SET en_text = "and that his efforts will be examined," WHERE id = 4823;
UPDATE quran_ayahs SET en_text = "then he will be recompensed in full," WHERE id = 4824;
