UPDATE quran_ayahs SET en_text = "and that He is the One Who makes people laugh and weep," WHERE id = 4826;
UPDATE quran_ayahs SET en_text = "and that He is the One Who causes death and gives life," WHERE id = 4827;
UPDATE quran_ayahs SET en_text = "and that He created the pairs, male and female," WHERE id = 4828;
UPDATE quran_ayahs SET en_text = "from an emitted drop of sperm," WHERE id = 4829;
UPDATE quran_ayahs SET en_text = "and it is upon Him to bring about the second creation." WHERE id = 4830;
