UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4967;
UPDATE quran_ayahs SET en_text = "In each there are fruits, palm trees, and pomegranates." WHERE id = 4968;
UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4969;
UPDATE quran_ayahs SET en_text = "In them there are beautiful noble women." WHERE id = 4970;
UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4971;
UPDATE quran_ayahs SET en_text = "Maidens with gorgeous eyes, secluded in pavilions." WHERE id = 4972;
UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4973;
UPDATE quran_ayahs SET en_text = "No human or jinn has ever touched them before." WHERE id = 4974;
UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4975;
UPDATE quran_ayahs SET en_text = "\\[They will be\\] reclining on green cushions and splendid carpets." WHERE id = 4976;
UPDATE quran_ayahs SET en_text = "Then which of the favors of your Lord will you \\[men and jinn\\] deny?" WHERE id = 4977;
UPDATE quran_ayahs SET en_text = "Blessed is the Name of your Lord, the Lord of Majesty and Honor." WHERE id = 4978;
UPDATE quran_ayahs SET en_text = "When the Inevitable Event comes to pass," WHERE id = 4979;
UPDATE quran_ayahs SET en_text = "there will be none to deny its occurrence;" WHERE id = 4980;
UPDATE quran_ayahs SET en_text = "it will bring some low and raise others up." WHERE id = 4981;
UPDATE quran_ayahs SET en_text = "When the earth will be shaken violently," WHERE id = 4982;
UPDATE quran_ayahs SET en_text = "and the mountains crushed to pieces," WHERE id = 4983;
UPDATE quran_ayahs SET en_text = "and turn to scattered dust," WHERE id = 4984;
UPDATE quran_ayahs SET en_text = "then you are sorted into three groups:" WHERE id = 4985;
UPDATE quran_ayahs SET en_text = "the people of the right – how blessed will be the people of the right!" WHERE id = 4986;
UPDATE quran_ayahs SET en_text = "The people of the left – how miserable will be the people of the left!" WHERE id = 4987;
UPDATE quran_ayahs SET en_text = "And the forerunners \\[in faith\\] are the forerunners \\[in Paradise\\]." WHERE id = 4988;
UPDATE quran_ayahs SET en_text = "They are the nearest ones \\[to Allah\\]," WHERE id = 4989;
UPDATE quran_ayahs SET en_text = "in Gardens of Bliss," WHERE id = 4990;
UPDATE quran_ayahs SET en_text = "many from the early generations," WHERE id = 4991;
UPDATE quran_ayahs SET en_text = "and a few from the later generations." WHERE id = 4992;
UPDATE quran_ayahs SET en_text = "\\[They will be seated\\] on jeweled couches," WHERE id = 4993;
UPDATE quran_ayahs SET en_text = "reclining on them, facing one another." WHERE id = 4994;
UPDATE quran_ayahs SET en_text = "They will be served by immortal youths" WHERE id = 4995;
UPDATE quran_ayahs SET en_text = "with cups, pitchers, and glasses \\[of wine\\] from a flowing spring," WHERE id = 4996;
UPDATE quran_ayahs SET en_text = "which will neither cause any headache nor intoxication," WHERE id = 4997;
UPDATE quran_ayahs SET en_text = "along with fruits of their choice," WHERE id = 4998;
UPDATE quran_ayahs SET en_text = "and flesh of birds that they may desire." WHERE id = 4999;
UPDATE quran_ayahs SET en_text = "And \\[they will have\\] maidens with wide gorgeous eyes," WHERE id = 5000;
UPDATE quran_ayahs SET en_text = "like pristine pearls," WHERE id = 5001;
UPDATE quran_ayahs SET en_text = "as a reward for what they used to do." WHERE id = 5002;
UPDATE quran_ayahs SET en_text = "They will not hear therein any idle talk or sinful speech," WHERE id = 5003;
UPDATE quran_ayahs SET en_text = "except the words of peace, peace." WHERE id = 5004;
UPDATE quran_ayahs SET en_text = "The people of the right – how blessed will be the people of the right!" WHERE id = 5005;
UPDATE quran_ayahs SET en_text = "\\[They will be\\] amid thornless lote trees," WHERE id = 5006;
UPDATE quran_ayahs SET en_text = "and clusters of bananas," WHERE id = 5007;
UPDATE quran_ayahs SET en_text = "and shade long-extended," WHERE id = 5008;
UPDATE quran_ayahs SET en_text = "and flowing water," WHERE id = 5009;
UPDATE quran_ayahs SET en_text = "and abundant fruit –" WHERE id = 5010;
UPDATE quran_ayahs SET en_text = "neither ceasing nor restricted –" WHERE id = 5011;
UPDATE quran_ayahs SET en_text = "and mattresses raised high \\[on beds\\]." WHERE id = 5012;
UPDATE quran_ayahs SET en_text = "Indeed, We have perfectly created them \\[their spouses\\]," WHERE id = 5013;
UPDATE quran_ayahs SET en_text = "and have made them virgins," WHERE id = 5014;
UPDATE quran_ayahs SET en_text = "loving and of equal age," WHERE id = 5015;
UPDATE quran_ayahs SET en_text = "for the people of the right," WHERE id = 5016;
UPDATE quran_ayahs SET en_text = "many from the earlier generations," WHERE id = 5017;
UPDATE quran_ayahs SET en_text = "and many from the later generations." WHERE id = 5018;
UPDATE quran_ayahs SET en_text = "And the people of the left – how wretched will be the people of the left!" WHERE id = 5019;
UPDATE quran_ayahs SET en_text = "\\[They will be\\] in scorching wind and scalding water," WHERE id = 5020;
UPDATE quran_ayahs SET en_text = "in the shade of black smoke," WHERE id = 5021;
UPDATE quran_ayahs SET en_text = "neither cool nor refreshing." WHERE id = 5022;
UPDATE quran_ayahs SET en_text = "Indeed, they used to be in luxuries before," WHERE id = 5023;
UPDATE quran_ayahs SET en_text = "and persisted in the gravest sin \\[of polytheism\\]." WHERE id = 5024;
UPDATE quran_ayahs SET en_text = "They used to say, \"When we die and become dust and bones, will we really be raised again?" WHERE id = 5025;
UPDATE quran_ayahs SET en_text = "And our forefathers as well?”" WHERE id = 5026;
UPDATE quran_ayahs SET en_text = "Say, \\[O Prophet\\], “Indeed, the former and later generations" WHERE id = 5027;
UPDATE quran_ayahs SET en_text = "will be gathered on a predetermined Day." WHERE id = 5028;
UPDATE quran_ayahs SET en_text = "Then you, O misguided deniers," WHERE id = 5029;
UPDATE quran_ayahs SET en_text = "will surely eat from the tree of zaqqūm," WHERE id = 5030;
UPDATE quran_ayahs SET en_text = "filling your bellies with it," WHERE id = 5031;
UPDATE quran_ayahs SET en_text = "and drinking scalding water on top of it," WHERE id = 5032;
UPDATE quran_ayahs SET en_text = "drinking like thirsty camels do.”" WHERE id = 5033;
UPDATE quran_ayahs SET en_text = "This will be their welcome on the Day of Judgment." WHERE id = 5034;
UPDATE quran_ayahs SET en_text = "It is We Who created you; will you not then believe \\[in resurrection\\]?" WHERE id = 5035;
UPDATE quran_ayahs SET en_text = "Have you thought about the semen that you emit?" WHERE id = 5036;
UPDATE quran_ayahs SET en_text = "Is it you who create it, or is it We Who are the Creators?" WHERE id = 5037;
UPDATE quran_ayahs SET en_text = "We have ordained death among you, and nothing can overcome us" WHERE id = 5038;
UPDATE quran_ayahs SET en_text = "from transforming you and recreating you in forms that you do not know." WHERE id = 5039;
UPDATE quran_ayahs SET en_text = "You are well aware of the first creation; why do you not then take heed?" WHERE id = 5040;
UPDATE quran_ayahs SET en_text = "Have you thought about what you sow?" WHERE id = 5041;
UPDATE quran_ayahs SET en_text = "Is it you who cause it grow, or is it We Who make it grow?" WHERE id = 5042;
UPDATE quran_ayahs SET en_text = "If We willed, We could reduce your harvest to crumble, leaving you to wonder and lament," WHERE id = 5043;
UPDATE quran_ayahs SET en_text = "“We suffered loss," WHERE id = 5044;
UPDATE quran_ayahs SET en_text = "rather, we are deprived.”" WHERE id = 5045;
UPDATE quran_ayahs SET en_text = "Have you thought about the water that you drink?" WHERE id = 5046;
UPDATE quran_ayahs SET en_text = "Is it you who bring it down from the clouds, or is it We Who bring it down?" WHERE id = 5047;
UPDATE quran_ayahs SET en_text = "If We willed, We could make it salty; will you not then give thanks?" WHERE id = 5048;
UPDATE quran_ayahs SET en_text = "Have you thought about the fire that you kindle?" WHERE id = 5049;
UPDATE quran_ayahs SET en_text = "Is it you who produced its trees, or is it We Who produced it?" WHERE id = 5050;
UPDATE quran_ayahs SET en_text = "We made it a reminder \\[of the Hellfire\\] and a benefit for the travelers." WHERE id = 5051;
UPDATE quran_ayahs SET en_text = "So glorify the Name of your Lord, the Most Great." WHERE id = 5052;
UPDATE quran_ayahs SET en_text = "I do swear by the positions of the stars –" WHERE id = 5053;
UPDATE quran_ayahs SET en_text = "and this is indeed a great oath, if only you knew –" WHERE id = 5054;
UPDATE quran_ayahs SET en_text = "that this is indeed a noble Qur’an," WHERE id = 5055;
UPDATE quran_ayahs SET en_text = "in a well-preserved Record," WHERE id = 5056;
UPDATE quran_ayahs SET en_text = "that none can touch except the purified \\[angels\\] –" WHERE id = 5057;
UPDATE quran_ayahs SET en_text = "a revelation from the Lord of the worlds." WHERE id = 5058;
UPDATE quran_ayahs SET en_text = "Do you take this discourse lightly," WHERE id = 5059;
UPDATE quran_ayahs SET en_text = "and instead \\[of thanking Allah\\] for the provision you are given, you show ingratitude?" WHERE id = 5060;
UPDATE quran_ayahs SET en_text = "Then why \\[are you helpless\\] when the soul reaches the throat," WHERE id = 5061;
UPDATE quran_ayahs SET en_text = "while you are looking on," WHERE id = 5062;
UPDATE quran_ayahs SET en_text = "when We are closer to him than you, but you do not see?" WHERE id = 5063;
UPDATE quran_ayahs SET en_text = "If you are not subject to retribution, then why do you not" WHERE id = 5064;
UPDATE quran_ayahs SET en_text = "bring that \\[soul\\] back, if you are truthful?" WHERE id = 5065;
UPDATE quran_ayahs SET en_text = "If that dying person is one of those who will be brought near to Allah," WHERE id = 5066;
UPDATE quran_ayahs SET en_text = "then \\[he will have\\] comfort, fragrance, and a Garden of Bliss." WHERE id = 5067;
UPDATE quran_ayahs SET en_text = "If he is one of the people of the right," WHERE id = 5068;
UPDATE quran_ayahs SET en_text = "\\[He will be told\\], “Peace be to you, for being one of the people of the right.”" WHERE id = 5069;
UPDATE quran_ayahs SET en_text = "But if he is one of the misguided deniers," WHERE id = 5070;
UPDATE quran_ayahs SET en_text = "then he will be welcomed with scalding water," WHERE id = 5071;
UPDATE quran_ayahs SET en_text = "and he will burn in Blazing Fire." WHERE id = 5072;
UPDATE quran_ayahs SET en_text = "This is indeed the absolute truth." WHERE id = 5073;
UPDATE quran_ayahs SET en_text = "So glorify the name of your Lord, the Most Great." WHERE id = 5074;
UPDATE quran_ayahs SET en_text = "All that is in the heavens and earth glorifies Allah, for He is the All-Mighty, the All-Wise." WHERE id = 5075;
UPDATE quran_ayahs SET en_text = "To Him belongs the dominion of the heavens and earth; He gives life and causes death, and He is Most Capable of all things." WHERE id = 5076;
UPDATE quran_ayahs SET en_text = "He is the First and the Last, the Manifest and the Hidden, and He is All-Knowing of everything." WHERE id = 5077;
UPDATE quran_ayahs SET en_text = "It is He Who created the heavens and earth in six days, and then rose over the Throne. He knows all that goes into the earth and all that comes out of it, and all that descends from the heaven and all that ascends to it. He is with you wherever you are, and Allah is All-Seeing of what you do." WHERE id = 5078;
UPDATE quran_ayahs SET en_text = "To Him belongs the dominion of the heavens and earth, and to Allah will return all matters." WHERE id = 5079;
UPDATE quran_ayahs SET en_text = "He causes the night to merge into the day and the day to merge into the night, and He is All-Knowing of what is in the hearts." WHERE id = 5080;
UPDATE quran_ayahs SET en_text = "Believe in Allah and His Messenger, and spend \\[in Allah’s cause\\] out of what He has entrusted you with. For those among you who believe and spend, there will be a great reward." WHERE id = 5081;
UPDATE quran_ayahs SET en_text = "Why is it that you do not believe in Allah when the Messenger is calling you to believe in your Lord, and He has already taken a covenant from you, if you will ever believe?" WHERE id = 5082;
UPDATE quran_ayahs SET en_text = "It is He Who sends down clear verses to His slave to bring you out of the depths of darkness into the light. Indeed, Allah is Ever Gracious and Most Merciful to you." WHERE id = 5083;
UPDATE quran_ayahs SET en_text = "Why is it that you do not spend in the cause of Allah, when Allah alone will inherit the heavens and earth? Those of you who spent and fought before the conquest \\[of Mecca and those who did not\\] are not equal. They are much greater in rank than those who spent and fought afterwards. Yet Allah has promised each a fine reward, and Allah is All-Aware of what you do." WHERE id = 5084;
UPDATE quran_ayahs SET en_text = "Who is there to lend Allah a goodly loan, so He will multiply it for him, and for him there will be a generous reward?" WHERE id = 5085;
UPDATE quran_ayahs SET en_text = "\\[Remember\\] the Day when you will see the believing men and women, with their light streaming ahead of them and on their right. \\[They will be told\\], “Glad tidings for you Today: gardens under which rivers flow, abiding therein forever.” That is the supreme triumph." WHERE id = 5086;
UPDATE quran_ayahs SET en_text = "On that Day the hypocrite men and women will say to those who believe, “Wait for us, so that we may have some of your light.” It will be said, “Go back and seek light elsewhere.” Then a wall will be placed between them, which will have a gate, on the inside of which there will be mercy and on the outside there will be punishment." WHERE id = 5087;
UPDATE quran_ayahs SET en_text = "The hypocrites will call out to the believers, “Were we not with you?” They will say, “Yes, but you allowed yourselves to succumb to temptations, you awaited \\[some misfortune to befall us\\], you doubted, and you were deceived by false hopes, until Allah’s decree came to pass. Thus the deceiver \\[Satan\\] deceived you concerning Allah." WHERE id = 5088;
UPDATE quran_ayahs SET en_text = "So this Day no ransom will be accepted from you or from the disbelievers. Your abode is the Fire; it is a fitting place for you – what a wretched destination!”" WHERE id = 5089;
UPDATE quran_ayahs SET en_text = "Has the time not yet come for those who believe that their hearts should be humbled at the remembrance of Allah and to the truth that has been revealed? And that they should not be like those who were given the Scripture before, whose hearts grew hard after the passage of a long time, and many of them were evildoers." WHERE id = 5090;
UPDATE quran_ayahs SET en_text = "Know that Allah revives the earth after its death. We have made the signs clear to you so that you may understand." WHERE id = 5091;
UPDATE quran_ayahs SET en_text = "Indeed, those men and women who give in charity and lend to Allah a goodly loan, it will be multiplied for them, and for them there will be a generous reward." WHERE id = 5092;
UPDATE quran_ayahs SET en_text = "Those who believe in Allah and His messengers, it is they who are the people of the truth. And the martyrs will have their reward and their light with their Lord. As for those who disbelieve and reject Our verses, they are the people of the Blazing Fire." WHERE id = 5093;
UPDATE quran_ayahs SET en_text = "Know that the life of this world is no more than play, amusement, adornment, boasting among yourselves, and a competition in wealth and children. It is like plants that flourish after rain, pleasing the sowers, then they wither and you see them turning yellow, then they crumble. And in the Hereafter there will be severe punishment, or forgiveness from Allah and His pleasure. For the life of this world is no more than a fleeting enjoyment." WHERE id = 5094;
UPDATE quran_ayahs SET en_text = "Race one another towards forgiveness from your Lord and Paradise the width of which is like the width of the heaven and earth, prepared for those who believe in Allah and His messengers. That is the bounty of Allah which He gives to whom He wills, for Allah is the Lord of great bounty." WHERE id = 5095;
UPDATE quran_ayahs SET en_text = "No calamity befalls the earth or yourselves but it is already written in a Record before We bring it into existence. That is indeed easy for Allah –" WHERE id = 5096;
UPDATE quran_ayahs SET en_text = "so that you may not grieve over what you have missed, nor exult over what He has given you, for Allah does not love anyone who is conceited and boastful –" WHERE id = 5097;
UPDATE quran_ayahs SET en_text = "those who are stingy and promote stinginess among people. And those who turn away, then Allah is the Self-Sufficient, the Praiseworthy." WHERE id = 5098;
UPDATE quran_ayahs SET en_text = "We have sent Our messengers with clear proofs, and sent down with them the Scripture and the criteria of justice, so that the people may uphold justice. And We sent down iron, in which there is great might and benefits for people, so that Allah may make known those who help His cause and His messengers without seeing Him. Indeed, Allah is All-Powerful, All-Mighty." WHERE id = 5099;
UPDATE quran_ayahs SET en_text = "We sent Noah and Abraham, and gave prophethood and Scripture to their offspring: some among them are guided, but most of them are evildoers." WHERE id = 5100;
UPDATE quran_ayahs SET en_text = "Then We sent Our messengers in their footsteps, and We sent after them Jesus, son of Mary; We gave him the Gospel, and instilled kindness and mercy in the hearts of those who followed him. As for monasticism, they invented it – We did not prescribe it for them – seeking thereby Allah’s pleasure, yet they did not observe it faithfully. So We gave those who believed among them their reward, but most of them are evildoers." WHERE id = 5101;
UPDATE quran_ayahs SET en_text = "O you who believe, fear Allah and believe in His Messenger; He will give you a double share of His mercy, and will give you a light by which you walk, and forgive you, for Allah is All-Forgiving, Most Merciful." WHERE id = 5102;
UPDATE quran_ayahs SET en_text = "\\[This is\\] so that the People of the Book may know that they have no power over anything of Allah’s bounty, and that all bounty is in the Hands of Allah; He gives it to whom He wills, for Allah is the Lord of great bounty." WHERE id = 5103;
UPDATE quran_ayahs SET en_text = "Indeed, Allah has heard the words of the woman who was arguing with you \\[O Prophet\\] concerning her husband, and was complaining to Allah. Allah has heard the discussion between the two of you, for Allah is All-Hearing, All-Seeing." WHERE id = 5104;
UPDATE quran_ayahs SET en_text = "As for those among you who divorce their wives by likening them to their mothers, they are not their mothers; their mothers are none but those who gave birth to them. They utter words that are abhorrent and false, but Allah is Most Pardoning, Most Forgiving." WHERE id = 5105;
UPDATE quran_ayahs SET en_text = "But those who divorce their wives by likening them to their mothers then retract what they said, they must free a slave before they are intimate with each other. This is what you are admonished, and Allah is All-Aware of what you do." WHERE id = 5106;
UPDATE quran_ayahs SET en_text = "But whoever cannot afford this must fast for two consecutive months before the couple have intimate relations with each other. But if he is unable to do that, he must feed sixty needy people. This is so that you may truly believe in Allah and His Messenger. These are the limits ordained by Allah, and for the disbelievers there will be a painful punishment." WHERE id = 5107;
UPDATE quran_ayahs SET en_text = "Indeed, those who oppose Allah and His Messenger will be debased, just as those who came before them were debased, for We have surely sent down clear signs. And for the disbelievers there will be a humiliating punishment." WHERE id = 5108;
UPDATE quran_ayahs SET en_text = "On the Day when Allah will resurrect them all together, and will inform them of what they did; Allah has kept their complete record, while they have forgotten it. For Allah is Witness over all things." WHERE id = 5109;
UPDATE quran_ayahs SET en_text = "Do you not see that Allah knows all that is in the heavens and all that is on earth? No secret consultation takes place between three, but He is their fourth, or between five, but He is their sixth, or between fewer or more than that, but He is with them wherever they may be. Then He will inform them of what they did on the Day of Resurrection. Indeed, Allah is All-Knowing of everything." WHERE id = 5110;
UPDATE quran_ayahs SET en_text = "Have you not seen those who were forbidden from holding secret talks, yet they go back to do what they were forbidden from, conspiring in sin and aggression, and in disobedience to the Messenger? When they come to you, they greet you other than the way Allah greets you, and they say to one another, “Why does Allah not punish us for what we say?” Sufficient is Hell for them; they will enter it. What a terrible destination!" WHERE id = 5111;
UPDATE quran_ayahs SET en_text = "O you who believe, when you hold secret counsel, do not do so in sin and aggression, and in disobedience to the Messenger, rather do so in righteousness and piety. And fear Allah, to Whom you will be gathered." WHERE id = 5112;
UPDATE quran_ayahs SET en_text = "Secret talks are only prompted by Satan to grieve the believers, but he cannot harm them in the least except with Allah’s permission. So let the believers put their trust in Allah." WHERE id = 5113;
UPDATE quran_ayahs SET en_text = "O you who believe, when you are told to make room in your gatherings, then make room; Allah will give you abundance. And when you are told to rise, then rise; Allah will raise in ranks those who believed from among you and those who are given knowledge. And Allah is All-Aware of what you do." WHERE id = 5114;
UPDATE quran_ayahs SET en_text = "O you who believe, when you consult the Messenger in private, give something in charity before your consultation; that is better for you and more conducive to purity. But if you do not have the means, then Allah is All-Forgiving, Most Merciful." WHERE id = 5115;
UPDATE quran_ayahs SET en_text = "Are you afraid of giving charities before your consultations with him? If you are unable to do so, and Allah has pardoned you, then establish prayer and give zakah, and obey Allah and His Messenger. And Allah is All-Aware of what you do." WHERE id = 5116;
UPDATE quran_ayahs SET en_text = "Do you not see those \\[hypocrites\\] who take as allies people who have incurred Allah’s wrath. They are neither of you nor of them, and they swear falsely while they know." WHERE id = 5117;
UPDATE quran_ayahs SET en_text = "Allah has prepared for them a severe punishment. Evil is indeed what they do." WHERE id = 5118;
UPDATE quran_ayahs SET en_text = "They have taken their oaths as a shield, in order to hinder people from the way of Allah. For them there will be a humiliating punishment." WHERE id = 5119;
UPDATE quran_ayahs SET en_text = "Neither their wealth nor their children will avail them anything against Allah. They are the people of the Fire; they will abide therein forever." WHERE id = 5120;
UPDATE quran_ayahs SET en_text = "On the Day when Allah will raise them all, they will swear to Him as they swear to you, thinking that they have something to stand on. Indeed, it is they who are the liars." WHERE id = 5121;
UPDATE quran_ayahs SET en_text = "Satan has taken hold of them and has thus caused them to forget the remembrance of Allah. They are the party of Satan. Indeed, the party of Satan will be the losers." WHERE id = 5122;
UPDATE quran_ayahs SET en_text = "Indeed, those who oppose Allah and His Messenger, they are among the most debased." WHERE id = 5123;
UPDATE quran_ayahs SET en_text = "Allah has decreed, “I and My messengers will surely prevail.” Indeed, Allah is All-Powerful, All-Mighty." WHERE id = 5124;
UPDATE quran_ayahs SET en_text = "You will not find any people who believe in Allah and the Last Day taking as allies those who oppose Allah and His Messenger, even if they were their parents, their children, their brothers, or their kindred. It is they in whose hearts Allah has instilled faith and strengthened them with a spirit from Him. He will admit them to gardens under which rivers flow, abiding therein forever. Allah is pleased with them and they are pleased with Allah. They are the party of Allah. Indeed, it is the party of Allah that will be the successful." WHERE id = 5125;
UPDATE quran_ayahs SET en_text = "All that is in the heavens and all that is on earth glorifies Allah, for He is the All-Mighty, the All-Wise." WHERE id = 5126;
UPDATE quran_ayahs SET en_text = "It is He Who expelled the disbelievers of the People of the Book \\[Banī Nadīr\\] from their homes as the first banishment. You did not think that they would leave, and they thought that their fortresses would protect them from Allah. But \\[the punishment of\\] Allah came upon them from where they did not expect and He cast dread in their hearts, destroying their homes with their own hands and the hands of the believers. So take a lesson, O you people of insight." WHERE id = 5127;
UPDATE quran_ayahs SET en_text = "Had Allah not decreed exile for them, He would have surely punished them in this world. And in the Hereafter, there will be for them the punishment of the Fire." WHERE id = 5128;
UPDATE quran_ayahs SET en_text = "That is because they opposed Allah and His Messenger. And whoever opposes Allah, Allah is truly severe in punishment." WHERE id = 5129;
UPDATE quran_ayahs SET en_text = "Whatever palm trees you cut down or left standing on their trunks, it was by Allah’s Will, so that He might disgrace the evildoers." WHERE id = 5130;
UPDATE quran_ayahs SET en_text = "Whatever gains Allah has bestowed upon His Messenger from them, you did not spur on any horses or camels for that, but Allah gives authority to His messengers over whom He wills, and Allah is Most Capable of all things." WHERE id = 5131;
UPDATE quran_ayahs SET en_text = "Whatever gains Allah has bestowed upon His Messenger from the people of the towns belongs to Allah and the Messenger, and to his close relatives, orphans, the needy, and stranded wayfarers, so that wealth should not circulate only among those of you who are rich. Whatever the Messenger gives you, accept it, and whatever he forbids you, refrain from it. And fear Allah; indeed, Allah is severe in punishment." WHERE id = 5132;
UPDATE quran_ayahs SET en_text = "For the poor emigrants \\[there is a due share\\] who were driven out of their homes and their properties, seeking Allah’s bounty and pleasure, and helping the cause of Allah and His Messenger. It is they who are the truthful." WHERE id = 5133;
UPDATE quran_ayahs SET en_text = "As for those who were settled in the city \\[of Madinah\\] and embraced Islam before them, they love those who migrated to them, and find no covetous desire in their hearts for what they were given. Rather they give them preference over themselves even though they may be in dire need. Whoever is protected from the covetousness of their souls, it is they who are successful." WHERE id = 5134;
UPDATE quran_ayahs SET en_text = "And those who came after them say, “Our Lord, forgive us and our brethren who preceded us in faith, and do not let there be any ill feelings in our hearts towards those who believe. Our Lord, indeed, You are All-Gracious, Most Merciful.”" WHERE id = 5135;
UPDATE quran_ayahs SET en_text = "Have you not seen the hypocrites who say to their fellow disbelievers from the People of the Book, “If you are expelled, we will surely leave with you, and we will never obey anyone concerning you, and if you are attacked, we will surely come to your aid.” But Allah bears witness that they are truly liars." WHERE id = 5136;
UPDATE quran_ayahs SET en_text = "If they are expelled, they will not leave with them, and if they are attacked, they will not come to their aid. Even if they come to their help, they will certainly turn and flee, then they will not be helped." WHERE id = 5137;
UPDATE quran_ayahs SET en_text = "Indeed, your fear in their hearts is greater than their fear of Allah. That is because they are a people devoid of understanding." WHERE id = 5138;
UPDATE quran_ayahs SET en_text = "They will not fight you all together but in fortified towns or from behind walls. Their valor among themselves is great; you think they are united, but their hearts are divided. That is because they are a people who do not understand." WHERE id = 5139;
UPDATE quran_ayahs SET en_text = "They are like those who were shortly before them, who tasted the evil consequence of their deeds, and for them there will be a painful punishment." WHERE id = 5140;
UPDATE quran_ayahs SET en_text = "They are like Satan when he says to man, “Disbelieve,” then when he disbelieves, he says, “I have nothing to do with you. I surely fear Allah, the Lord of the worlds.”" WHERE id = 5141;
UPDATE quran_ayahs SET en_text = "So they will both end up in the Fire, abiding therein forever. That is the recompense of the wrongdoers." WHERE id = 5142;
UPDATE quran_ayahs SET en_text = "O you who believe, fear Allah, and let every soul consider what it has sent forth for tomorrow. And fear Allah. Indeed, Allah is All-Aware of what you do." WHERE id = 5143;
UPDATE quran_ayahs SET en_text = "Do not be like those who forgot Allah, so He made them forget themselves. It is they who are the evildoers." WHERE id = 5144;
UPDATE quran_ayahs SET en_text = "The people of Hell and the people of Paradise are not equal. It is the people of Paradise who will triumph." WHERE id = 5145;
UPDATE quran_ayahs SET en_text = "Had We sent down this Qur’an upon a mountain, you would have seen it humbled and break asunder out of awe of Allah. Such are the similitudes We set forth for people so that they may reflect." WHERE id = 5146;
UPDATE quran_ayahs SET en_text = "He is Allah; none has the right to be worshiped except Him, Knower of the unseen and the seen; He is the Most Compassionate, the Most Merciful." WHERE id = 5147;
UPDATE quran_ayahs SET en_text = "He is Allah; none has the right to be worshiped except Him, the Sovereign, the Most Holy, the Most Perfect, the Granter of Security, the Watcher over all, the All-Mighty, the Compeller, the Supreme. Glory be to Allah far above what they associate with Him." WHERE id = 5148;
UPDATE quran_ayahs SET en_text = "He is Allah, the Creator, the Maker, the Fashioner. He has the most beautiful names. All that is in the heavens and earth glorifies Him, for He is the All-Mighty, the All-Wise." WHERE id = 5149;
UPDATE quran_ayahs SET en_text = "O you who believe, do not take My enemies and your enemies as allies, offering them friendship even though they rejected the truth that has come to you, and they have driven out the Messenger and yourselves \\[from Makkah\\] simply because you believe in Allah, your Lord – if you have emigrated to struggle in My cause and seek My pleasure. How can you disclose secrets \\[of the believers\\], showing them friendship, when I know best all that you conceal and all that you reveal? Whoever among you does that has truly strayed from the straight path." WHERE id = 5150;
UPDATE quran_ayahs SET en_text = "If they gain the upper hand over you, they would be your \\[open\\] enemies, and would stretch their hands and tongues towards you to harm you, and they wish that you too would disbelieve." WHERE id = 5151;
UPDATE quran_ayahs SET en_text = "Neither your relatives nor your children will benefit you on the Day of Resurrection; He will judge between you, and Allah is All-Seeing of what you do." WHERE id = 5152;
UPDATE quran_ayahs SET en_text = "Indeed, you have a good example in Abraham and those with him, when they said to their people, “We dissociate ourselves from you and whatever you worship besides Allah. We renounce you, and the enmity and hatred have arisen between us and you forever until you believe in Allah alone”, except for Abraham when he said to his father, “I will seek forgiveness for you, but I cannot protect you from Allah in the least.” “Our Lord, in You we put our trust and to You we turn, and to You is the final return.”" WHERE id = 5153;
UPDATE quran_ayahs SET en_text = "Our Lord, do not make us subject to the persecution of those who disbelieve; and forgive us, our Lord. Indeed, You are the All-Mighty, the All-Wise.”" WHERE id = 5154;
UPDATE quran_ayahs SET en_text = "You certainly have a good example in them, for those who look forward with hope to Allah and the Last Day. But whoever turns away, Allah is indeed the Self-Sufficient, the Praiseworthy." WHERE id = 5155;
UPDATE quran_ayahs SET en_text = "It may be that Allah will bring about friendship between you and those of them whom you hold as enemies. Allah is Most Capable, and Allah is All-Forgiving, Most Merciful." WHERE id = 5156;
UPDATE quran_ayahs SET en_text = "Allah does not forbid you from being kind and just to those who did not fight against you nor did they drive you out of your homes. Indeed, Allah loves those who are just." WHERE id = 5157;
UPDATE quran_ayahs SET en_text = "Allah only forbids you from taking as allies those who fought against you on account of your religion, and drove you out of your homes or helped others in driving you out. Whoever takes them as allies, it is they who are the wrongdoers." WHERE id = 5158;
UPDATE quran_ayahs SET en_text = "O you who believe, when the believing women come to you as migrants, test their sincerity, although Allah knows best about their faith. Then if you find them to be believers, do not send them back to the disbelievers; they are not lawful \\[wives\\] for them, nor are they lawful \\[husbands\\] for them. But give the disbelievers whatever \\[dowries\\] they have spent. And there is no blame on you if you marry them, as long as you give them their dowries. Do not hold on to the marriage ties with the disbelieving women; ask for repayment of whatever \\[dowries\\] you have spent, and let the disbelievers ask for repayment of whatever \\[dowries\\] they have spent. This is the ruling of Allah that He judges between you, and Allah is All-Knowing, All-Wise." WHERE id = 5159;
UPDATE quran_ayahs SET en_text = "If any of your wives desert you and join the disbelievers \\[without returning your dowries\\], and later you take spoils from them, then give to those whose wives have deserted them the equivalent of what they had spent. And fear Allah, in Whom you believe." WHERE id = 5160;
UPDATE quran_ayahs SET en_text = "O Prophet, when the believing women come to you, pledging to you that they will not associate anything with Allah, that they will not steal, that they will not commit fornication, that they will not kill their children, that they will not falsely attribute \\[illegitimate\\] children to their husbands between their arms and their legs, that they will not disobey you in what is right, then accept their pledge and ask Allah to forgive them. Indeed, Allah is All-Forgiving, Most Merciful." WHERE id = 5161;
UPDATE quran_ayahs SET en_text = "O you who believe, do not take as allies a people whom Allah is displeased with. They have lost hope in the Hereafter, just as the disbelievers have lost hope in those who are in the graves." WHERE id = 5162;
UPDATE quran_ayahs SET en_text = "All that is in the heavens and all that is on earth glorifies Allah, for He is the All-Mighty, the All-Wise." WHERE id = 5163;
UPDATE quran_ayahs SET en_text = "O you who believe, why do you say what you do not do?" WHERE id = 5164;
UPDATE quran_ayahs SET en_text = "It is most loathsome to Allah that you say what you do not do." WHERE id = 5165;
UPDATE quran_ayahs SET en_text = "Indeed, Allah loves those who fight in His way in firm ranks, as if they were a solid structure." WHERE id = 5166;
UPDATE quran_ayahs SET en_text = "And \\[remember\\] when Moses said to his people, “O my people, why do you hurt me when you know that I am a messenger of Allah to you?” So when they chose to deviate, Allah caused their hearts to deviate, for Allah does not guide the rebellious people." WHERE id = 5167;
UPDATE quran_ayahs SET en_text = "And \\[remember\\] when Jesus, son of Mary, said, “O Children of Israel, I am truly a messenger of Allah to you, confirming the Torah which came before me, and giving glad tidings of a messenger after me whose name will be Ahmad.” But when he came to them with clear proofs, they said, “This is clear magic.”" WHERE id = 5168;
UPDATE quran_ayahs SET en_text = "Who could be more wrong than the one who fabricates lies against Allah when invited to Islam? And Allah does not guide the wrongdoing people." WHERE id = 5169;
UPDATE quran_ayahs SET en_text = "They wish to extinguish the light of Allah with their mouths, but Allah will complete His light, even though the disbelievers dislike it." WHERE id = 5170;
UPDATE quran_ayahs SET en_text = "It is He Who has sent His Messenger with guidance and the religion of truth, so that He may make it prevail over all religions, even though the polytheists may dislike it." WHERE id = 5171;
UPDATE quran_ayahs SET en_text = "O you who believe, shall I tell you about a trade that will save you from a painful punishment?" WHERE id = 5172;
UPDATE quran_ayahs SET en_text = "It is to believe in Allah and His Messenger, and to struggle in the way of Allah with your wealth and your lives. That is better for you, if only you knew." WHERE id = 5173;
UPDATE quran_ayahs SET en_text = "He will forgive you your sins and admit you to gardens under which rivers flow, and pleasant dwellings in Gardens of Eternity. That is the supreme triumph." WHERE id = 5174;
UPDATE quran_ayahs SET en_text = "And \\[He will grant you\\] another favor that you love: help from Allah \\[against your enemies\\] and an imminent conquest. Give glad tidings to the believers." WHERE id = 5175;
UPDATE quran_ayahs SET en_text = "O you who believe, be supporters of Allah’s cause, as Jesus, son of Mary, said to the disciples, “Who will be my supporters for Allah’s cause?” The disciples said, “We are the supporters for Allah’s cause.” Then a group of the Children of Israel believed and another disbelieved. We then supported those who believed against their enemies, so they prevailed." WHERE id = 5176;
UPDATE quran_ayahs SET en_text = "All that is in the heavens and all that is on earth glorifies Allah, the Sovereign, the Most Holy, the All-Mighty, the All-Wise." WHERE id = 5177;
UPDATE quran_ayahs SET en_text = "It is He Who has sent among the unlettered \\[Arabs\\] a messenger from among themselves, reciting to them His verses and purifying them, and teaching them the Book and Wisdom \\[sunnah\\], although before that they were clearly misguided." WHERE id = 5178;
UPDATE quran_ayahs SET en_text = "And \\[he is also sent\\] to others of them who have not yet joined them, and He is the All-Mighty, the All-Wise." WHERE id = 5179;
UPDATE quran_ayahs SET en_text = "That is the bounty of Allah that He gives to whom He wills, for Allah is the Lord of great bounty." WHERE id = 5180;
UPDATE quran_ayahs SET en_text = "The similitude of those who were given the Torah but failed to uphold it, is like that of a donkey laden with books. How terrible is the similitude of those who reject the verses of Allah! And Allah does not guide the wrongdoing people." WHERE id = 5181;
UPDATE quran_ayahs SET en_text = "Say, “O you who are Jews, if you claim that you alone are Allah’s allies apart from all other people, then wish for death, if you are truthful." WHERE id = 5182;
UPDATE quran_ayahs SET en_text = "But they will never wish for it because of what their hands have sent forth. And Allah is All-Knowing of the wrongdoers." WHERE id = 5183;
UPDATE quran_ayahs SET en_text = "Say, “The death from which you run away will surely overcome you, then you will be brought back to the Knower of the unseen and the seen, and He will inform you of what you used to do.”" WHERE id = 5184;
UPDATE quran_ayahs SET en_text = "O you who believe, when the call for prayer is made on Friday, then hasten to the remembrance of Allah and leave off trading. That is better for you, if only you knew." WHERE id = 5185;
UPDATE quran_ayahs SET en_text = "When the prayer is over, disperse in the land and seek from the bounty of Allah, and remember Allah much so that you may be successful." WHERE id = 5186;
UPDATE quran_ayahs SET en_text = "When they see some merchandise or amusement, they rush towards it and leave you standing. Say, “That which is with Allah is better than amusement and merchandise, and Allah is the Best of Providers.”" WHERE id = 5187;
UPDATE quran_ayahs SET en_text = "When the hypocrites come to you, they say, “We bear witness that you are indeed the Messenger of Allah” And Allah knows that you are indeed His Messenger, and Allah bears witness that the hypocrites are truly liars." WHERE id = 5188;
UPDATE quran_ayahs SET en_text = "They have taken their oaths as a shield in order to hinder people from the way of Allah. Evil indeed is what they do!" WHERE id = 5189;
UPDATE quran_ayahs SET en_text = "That is because they believed, then disbelieved; therefore their hearts have been sealed, so they do not understand." WHERE id = 5190;
UPDATE quran_ayahs SET en_text = "When you see them, their outward appearance pleases you, and when they speak, you listen to what they say. But they are like planks of wood leaned \\[against the wall\\]. They deem every cry to be against them. They are the enemy, so beware of them. May Allah destroy them; how are they deluded?" WHERE id = 5191;
UPDATE quran_ayahs SET en_text = "When it is said to them, “Come, so that the Messenger of Allah may seek forgiveness for you”, they shake their heads, and you see them turning away in arrogance." WHERE id = 5192;
UPDATE quran_ayahs SET en_text = "It is the same to them whether you seek forgiveness for them or not, Allah will never forgive them. Indeed, Allah does not guide the wicked people." WHERE id = 5193;
UPDATE quran_ayahs SET en_text = "They are those who say, “Do not spend on those who are with Allah’s Messenger so that they may desert him. But to Allah belong the treasures of the heavens and earth, but the hypocrites do not understand." WHERE id = 5194;
UPDATE quran_ayahs SET en_text = "They say, “When we return to Madinah, the mightier will surely expel the weaker therefrom.” In fact, all might belongs to Allah and to His Messenger, and to the believers, but the hypocrites do not know." WHERE id = 5195;
UPDATE quran_ayahs SET en_text = "O you who believe, do not let your wealth and your children distract you from Allah’s remembrance. For whoever does that, it is they who are the losers." WHERE id = 5196;
UPDATE quran_ayahs SET en_text = "And spend from what We have provided for you before death comes to one of you, then he would say, “My Lord, If only You had delayed me for a short while, so that I would give in charity and be one of the righteous.”" WHERE id = 5197;
UPDATE quran_ayahs SET en_text = "But Allah will never delay a soul when its appointed time comes, and Allah is All-Aware of what you do." WHERE id = 5198;
UPDATE quran_ayahs SET en_text = "All that is in the heavens and all that is in the earth glorifies Allah. To Him belongs the dominion, and to Him belongs all praise, and He is Most Capable of all things." WHERE id = 5199;
UPDATE quran_ayahs SET en_text = "It is He Who created you, yet some of you are disbelievers and some of you are believers. And Allah is All-Seeing of what you do." WHERE id = 5200;
UPDATE quran_ayahs SET en_text = "He created the heavens and earth for a true purpose, He shaped you and perfected your form. And to Him is the final return." WHERE id = 5201;
UPDATE quran_ayahs SET en_text = "He knows all that is in the heavens and earth, and He knows whatever you conceal and whatever you reveal. And Allah is All-Knowing of what is in the hearts." WHERE id = 5202;
UPDATE quran_ayahs SET en_text = "Has there not come to you the stories of those who disbelieved before you? They tasted the evil consequence of their deeds, and for them there will be a painful punishment." WHERE id = 5203;
UPDATE quran_ayahs SET en_text = "That is because their messengers came to them with clear proofs, but they said, “How can a human guide us?” Thus they disbelieved and turned away, but Allah had no need for them, for Allah is Self-Sufficient, Praiseworthy." WHERE id = 5204;
UPDATE quran_ayahs SET en_text = "The disbelievers claim that they will never be resurrected. Say, “Yes, by my Lord, you will surely be resurrected, then you will surely be informed of what you did. And that is easy for Allah.”" WHERE id = 5205;
UPDATE quran_ayahs SET en_text = "So believe in Allah and His Messenger, and in the Light \\[the Qur’an\\] that We have sent down. And Allah is All-Aware of what you do." WHERE id = 5206;
UPDATE quran_ayahs SET en_text = "When He will gather you for the Gathering Day; that is the Day of great loss. But whoever believes in Allah and does righteous deeds, He will absolve them of their bad deeds and will admit them to gardens under which rivers flow, abiding therein forever. That is the supreme triumph." WHERE id = 5207;
UPDATE quran_ayahs SET en_text = "As for those who disbelieve and reject Our verses, they will be the people of the Fire, abiding therein forever. What a terrible destination!" WHERE id = 5208;
UPDATE quran_ayahs SET en_text = "No calamity befalls except with Allah’s permission. Whoever believes in Allah, He will guide his heart. And Allah is All-Knowing of everything." WHERE id = 5209;
UPDATE quran_ayahs SET en_text = "Obey Allah and obey the Messenger, but if you turn away, then the duty of Our Messenger is only to convey the message clearly." WHERE id = 5210;
UPDATE quran_ayahs SET en_text = "Allah, none has the right to be worshiped except Him. So in Allah let the believers put their trust." WHERE id = 5211;
UPDATE quran_ayahs SET en_text = "O you who believe, indeed, among your wives and your children are some who are enemies to you, so beware of them. But if you pardon and overlook, and forgive, then Allah is indeed All-Forgiving, Most Merciful." WHERE id = 5212;
UPDATE quran_ayahs SET en_text = "Your wealth and your children are but a trial, and with Allah is a great reward." WHERE id = 5213;
UPDATE quran_ayahs SET en_text = "So fear Allah as much as you can, listen and obey, and spend in charity; that is better for your souls. Whoever is protected from the stinginess of his soul, it is they who are successful." WHERE id = 5214;
UPDATE quran_ayahs SET en_text = "If you lend Allah a goodly loan, He will multiply it for you and forgive you, for Allah is Most Appreciative, Most Forbearing," WHERE id = 5215;
UPDATE quran_ayahs SET en_text = "Knower of the unseen and the seen, the All-Mighty, the All-Wise." WHERE id = 5216;
UPDATE quran_ayahs SET en_text = "O Prophet, when you \\[believers\\] divorce women, divorce them with concern to their waiting period and keep an accurate count of the waiting period, and fear Allah, your Lord. Do not force them out of their houses, nor should they leave unless they commit a clear shameful act. Such are the limits ordained by Allah. Whoever transgresses the limits ordained by Allah has truly wronged himself. You never know, perhaps Allah will bring about a change \\[of heart\\] later." WHERE id = 5217;
UPDATE quran_ayahs SET en_text = "Then when they have approached the end of their waiting period, either retain them honorably or part with them honorably. Call two just men from among yourselves as witnesses, and give true testimony for the sake of Allah. This is an exhortation for those who believe in Allah and the Last Day. Whoever fears Allah, He will make a way out for him," WHERE id = 5218;
UPDATE quran_ayahs SET en_text = "and He will provide for him from where he does not expect. Whoever puts his trust in Allah, He is sufficient for him. Indeed, Allah will surely accomplish His purpose, for Allah has set a destiny for everything." WHERE id = 5219;
UPDATE quran_ayahs SET en_text = "As for those women from among you who reached their menopause, in case you doubt, their waiting period is three months, as well as of those who have not yet menstruated. As for pregnant women, their waiting period ends with delivery. And whoever fears Allah, He will make his matters easy for him." WHERE id = 5220;
UPDATE quran_ayahs SET en_text = "This is the commandment of Allah that He has sent down to you. Whoever fears Allah, He will absolve him of his bad deeds and will reward him immensely." WHERE id = 5221;
UPDATE quran_ayahs SET en_text = "Let them live where you live \\[during their waiting period\\], according to your means, and do not harass them to make their stay unbearable. If they are pregnant, maintain them until they deliver, and if they suckle your child, compensate them, and graciously settle the question of compensation by mutual understanding. But if you fail to reach an agreement, then another woman may suckle the child." WHERE id = 5222;
UPDATE quran_ayahs SET en_text = "Let the wealthy man spend according to his means. As for the one with limited resources, he should spend according to whatever Allah has given him. Allah does not burden any soul beyond what He has given him. Allah will bring about ease after hardship." WHERE id = 5223;
UPDATE quran_ayahs SET en_text = "And \\[imagine\\] how many towns rebelled against the commandments of their Lord and His messengers, so We called them to a severe account and punished them with a horrendous punishment." WHERE id = 5224;
UPDATE quran_ayahs SET en_text = "Thus they tasted the evil consequence of their deeds, and the outcome of their deeds was a total loss." WHERE id = 5225;
UPDATE quran_ayahs SET en_text = "Allah has prepared for them a severe punishment. So fear Allah O people of understanding who have faith. Allah has sent down to you a Reminder," WHERE id = 5226;
UPDATE quran_ayahs SET en_text = "a Messenger who recites to you the clear verses of Allah, so that He may bring those who believe and do righteous deeds out of the depths of darkness into the light. Whoever believes in Allah and does righteous deeds, He will admit him to gardens under which rivers flow, abiding therein forever. Allah has prepared for him a good provision." WHERE id = 5227;
UPDATE quran_ayahs SET en_text = "Allah is the One who has created seven heavens, and likewise for the earth. The Command descends between them so that you may know that Allah is Most Capable of all things, and that Allah has encompassed everything in knowledge." WHERE id = 5228;
UPDATE quran_ayahs SET en_text = "O Prophet, why do you prohibit \\[yourself from\\] what Allah has made lawful to you, seeking to please your wives? Allah is All-Forgiving, Most Merciful." WHERE id = 5229;
UPDATE quran_ayahs SET en_text = "Allah has ordained for you the way to absolve yourselves from your oaths. Allah is your Guardian, and He is the All-Knowing, the All-Wise." WHERE id = 5230;
UPDATE quran_ayahs SET en_text = "And \\[remember\\] when the Prophet told one of his wives something in secret. Then when she disclosed it \\[to another wife\\], and Allah made it known to him, he told \\[the disclosing wife\\] part of what was disclosed and overlooked a part. When he informed her of that, she said, “Who informed you of this?” He said, “I was informed by the All-Knowing, the All-Aware." WHERE id = 5231;
UPDATE quran_ayahs SET en_text = "You both had better turn to Allah in repentance, for your hearts have faltered. But if you insist on conspiring against him, then indeed Allah is his Protector, as well as Gabriel and the righteous believers, and moreover, the angels are his supporters." WHERE id = 5232;
UPDATE quran_ayahs SET en_text = "Perhaps, if he were to divorce you all, his Lord would replace you with better wives who are submissive to Allah, who are believers, obedient, repentant, devout in worship and fasting – previously married and virgins." WHERE id = 5233;
UPDATE quran_ayahs SET en_text = "O you who believe, protect yourselves and your families from a Fire whose fuel is people and stones, and is overseen by rigorous and stern angels, who never disobey whatever Allah commands and do whatever they are commanded." WHERE id = 5234;
UPDATE quran_ayahs SET en_text = "“O you who disbelieve, make no excuses this Day. You will only be recompensed for what you used to do.”" WHERE id = 5235;
UPDATE quran_ayahs SET en_text = "O you who believe, turn to Allah in sincere repentance. It may be that your Lord will absolve you of your bad deeds and admit you to gardens under which rivers flow, on the Day when Allah will not disgrace the Prophet and those who believed with him. Their light will stream ahead of them and on their right. They will say, “Our Lord, complete our light for us and forgive us, for You are Most Capable of all things.”" WHERE id = 5236;
UPDATE quran_ayahs SET en_text = "O Prophet, strive \\[and fight\\] against the disbelievers and the hypocrites, and be harsh with them. Their abode will be Hell. What a terrible destination!" WHERE id = 5237;
UPDATE quran_ayahs SET en_text = "Allah sets forth an example for the disbelievers: the wife of Noah and the wife of Lot, who were married to two of Our righteous slaves, but they betrayed them. Their husbands could not avail them anything against Allah, and they both were told, “Enter the Fire along with those who enter!”" WHERE id = 5238;
UPDATE quran_ayahs SET en_text = "And Allah sets forth an example for the believers: the wife of Pharaoh, who said, “My Lord, build for me a house in Paradise near You, save me from Pharaoh and his \\[evil\\] deeds, and save me from the wrongdoing people.”" WHERE id = 5239;
UPDATE quran_ayahs SET en_text = "Also \\[the example of\\] Mary, daughter of ‘Imrān who guarded her chastity, so We breathed into her through Our angel \\[Gabriel\\], and she firmly believed in the words of her Lord and His Scriptures, and was one of the obedient." WHERE id = 5240;
UPDATE quran_ayahs SET en_text = "Blessed is He in Whose Hand is the dominion, and He is Most Capable of all things." WHERE id = 5241;
UPDATE quran_ayahs SET en_text = "Who created death and life to test you as to which of you is best in deeds, and He is the All-Mighty, the Most Forgiving." WHERE id = 5242;
UPDATE quran_ayahs SET en_text = "Who created seven heavens one above another; you will not see any imperfection in the creation of the Most Compassionate. Look again; can you see any flaw?" WHERE id = 5243;
UPDATE quran_ayahs SET en_text = "Then look again and again; your sight will turn back to you humbled and weary." WHERE id = 5244;
UPDATE quran_ayahs SET en_text = "We have adorned the lowest heaven with lamps and have made them as missiles to stone the devils, and We have prepared for them the punishment of the Blazing Fire." WHERE id = 5245;
UPDATE quran_ayahs SET en_text = "For those who disbelieve in their Lord there will be the punishment of Hell. What a terrible destination!" WHERE id = 5246;
UPDATE quran_ayahs SET en_text = "When they are thrown in it, they will hear its roaring as it boils up," WHERE id = 5247;
UPDATE quran_ayahs SET en_text = "almost bursting in fury. Every time a group is thrown in it, its keepers will ask them, “Did there not come to you a warner?”" WHERE id = 5248;
UPDATE quran_ayahs SET en_text = "They will say, “Yes, a warner did come to us, but we denied and said, ‘Allah has not sent down anything; you are greatly misguided.’”" WHERE id = 5249;
UPDATE quran_ayahs SET en_text = "And they will say, “If only we had listened or understood, we would not be among the dwellers of the Blazing Fire.”" WHERE id = 5250;
UPDATE quran_ayahs SET en_text = "Thus they will confess their sins. So away with the dwellers of the Blazing Fire!" WHERE id = 5251;
UPDATE quran_ayahs SET en_text = "Indeed, those who fear their Lord unseen will have forgiveness and a great reward." WHERE id = 5252;
UPDATE quran_ayahs SET en_text = "Whether you speak secretly or openly, He is All-Knowing of that which is in the hearts." WHERE id = 5253;
UPDATE quran_ayahs SET en_text = "Does He not know His Own creation, when He is the Most Subtle, the All-Aware?" WHERE id = 5254;
UPDATE quran_ayahs SET en_text = "It is He Who made the earth smooth for you, so travel through its regions and eat from His provisions. And to Him is the resurrection." WHERE id = 5255;
UPDATE quran_ayahs SET en_text = "Do you feel secure that He Who is \\[above\\] in heaven will not cause the earth to sink with you, then suddenly convulse?" WHERE id = 5256;
UPDATE quran_ayahs SET en_text = "Do you feel secure that He Who is in heaven will not send against you a storm of stones? Only then would you know how \\[serious\\] My warning was!" WHERE id = 5257;
UPDATE quran_ayahs SET en_text = "Those who came before them rejected \\[the messengers\\]; then how severe was My response!" WHERE id = 5258;
UPDATE quran_ayahs SET en_text = "Do they not see the birds above them, spreading out and folding in their wings? None holds them up except the Most Compassionate. He is indeed All-Seeing of everything." WHERE id = 5259;
UPDATE quran_ayahs SET en_text = "Which is your army that can come to your aid against the Most Compassionate? The disbelievers are in utter delusion." WHERE id = 5260;
UPDATE quran_ayahs SET en_text = "Who is there to give you provision if He withholds His provision? Yet they persist in arrogance and aversion." WHERE id = 5261;
UPDATE quran_ayahs SET en_text = "Who is better guided: the one who walks stumbling and falling on his face or the one who walks upright on a straight path?" WHERE id = 5262;
UPDATE quran_ayahs SET en_text = "Say, “It is He Who brought you into being and gave you hearing, sight, and hearts; little do you give thanks.”" WHERE id = 5263;
UPDATE quran_ayahs SET en_text = "Say, “It is He Who has dispersed you throughout the earth, and to Him you will be gathered.”" WHERE id = 5264;
UPDATE quran_ayahs SET en_text = "They say: “When will this promise come to pass, if you are truthful?”" WHERE id = 5265;
UPDATE quran_ayahs SET en_text = "Say, “That knowledge is with Allah alone, and I am only a clear warner.”" WHERE id = 5266;
UPDATE quran_ayahs SET en_text = "Then when they see it approaching closer, the faces of the disbelievers will be distressed, and it will be said, “This is what you were asking for.”" WHERE id = 5267;
UPDATE quran_ayahs SET en_text = "Say, “What do you think, if Allah causes me and those who are with me to die or shows us mercy, who will save the disbelievers from a painful punishment?”" WHERE id = 5268;
UPDATE quran_ayahs SET en_text = "Say, “He is the Most Compassionate; we believe in Him and put our trust in Him. You will come to know who is clearly misguided.”" WHERE id = 5269;
UPDATE quran_ayahs SET en_text = "Say, “What do you think, if your water were to sink deep into the earth, who can bring you flowing water?”" WHERE id = 5270;
UPDATE quran_ayahs SET en_text = "Nūn. By the pen and what they \\[the angels\\] write \\[in the Records of men\\]," WHERE id = 5271;
UPDATE quran_ayahs SET en_text = "By the grace of your Lord \\[O Prophet\\], you are not a madman." WHERE id = 5272;
UPDATE quran_ayahs SET en_text = "You will surely have a never-ending reward." WHERE id = 5273;
UPDATE quran_ayahs SET en_text = "Indeed, you are of a great moral character." WHERE id = 5274;
UPDATE quran_ayahs SET en_text = "Soon you will see and they will see," WHERE id = 5275;
UPDATE quran_ayahs SET en_text = "which of you is afflicted with madness." WHERE id = 5276;
UPDATE quran_ayahs SET en_text = "Indeed, your Lord alone knows best who has strayed from His way and those who are rightly guided." WHERE id = 5277;
UPDATE quran_ayahs SET en_text = "So do not obey the deniers." WHERE id = 5278;
UPDATE quran_ayahs SET en_text = "They wish that you would compromise so they would too compromise." WHERE id = 5279;
UPDATE quran_ayahs SET en_text = "And do not obey every disgraceful swearer," WHERE id = 5280;
UPDATE quran_ayahs SET en_text = "backbiter, gossip-monger," WHERE id = 5281;
UPDATE quran_ayahs SET en_text = "withholder of good, transgressor, sinful," WHERE id = 5282;
UPDATE quran_ayahs SET en_text = "coarse, and furthermore, an illegitimate child." WHERE id = 5283;
UPDATE quran_ayahs SET en_text = "Simply because he has wealth and children," WHERE id = 5284;
UPDATE quran_ayahs SET en_text = "When Our verses are recited to him, he says, “Ancient fables!”" WHERE id = 5285;
UPDATE quran_ayahs SET en_text = "We will soon brand him on the snout." WHERE id = 5286;
UPDATE quran_ayahs SET en_text = "Indeed, We have tested them \\[the Makkans\\] as We tested the people of the garden, when they swore to harvest its fruits in the early morning," WHERE id = 5287;
UPDATE quran_ayahs SET en_text = "and made no exception \\[by saying: if Allah wills\\]." WHERE id = 5288;
UPDATE quran_ayahs SET en_text = "Then it was struck by an affliction from your Lord while they were asleep," WHERE id = 5289;
UPDATE quran_ayahs SET en_text = "and it turned to ashes as black as night." WHERE id = 5290;
UPDATE quran_ayahs SET en_text = "Then in the early morning they called out to one another:" WHERE id = 5291;
UPDATE quran_ayahs SET en_text = "“Go early to your garden if you really wish to harvest.”" WHERE id = 5292;
UPDATE quran_ayahs SET en_text = "So they set out, whispering to one another," WHERE id = 5293;
UPDATE quran_ayahs SET en_text = "“Not a single poor person should enter your garden today.”" WHERE id = 5294;
UPDATE quran_ayahs SET en_text = "And they left early, fully determined to stick to their plan." WHERE id = 5295;
UPDATE quran_ayahs SET en_text = "But when they saw the garden, they said, “We must have lost our way!" WHERE id = 5296;
UPDATE quran_ayahs SET en_text = "Rather, we have been deprived.”" WHERE id = 5297;
UPDATE quran_ayahs SET en_text = "The best among them said, “Did I not say to you to glorify Allah?”" WHERE id = 5298;
UPDATE quran_ayahs SET en_text = "They said, “Glory be to our Lord! We were truly wrongdoers.”" WHERE id = 5299;
UPDATE quran_ayahs SET en_text = "Then they turned to one another, throwing blame." WHERE id = 5300;
UPDATE quran_ayahs SET en_text = "They said, “Woe to us! We were surely transgressors." WHERE id = 5301;
UPDATE quran_ayahs SET en_text = "Our Lord may give us something better than this. We certainly turn to our Lord in hope.”" WHERE id = 5302;
UPDATE quran_ayahs SET en_text = "Such is the punishment \\[in this world\\]; but the punishment of the Hereafter is far worse, if only they knew." WHERE id = 5303;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will have gardens of bliss with their Lord." WHERE id = 5304;
UPDATE quran_ayahs SET en_text = "Should We then treat Muslims like the wicked?" WHERE id = 5305;
UPDATE quran_ayahs SET en_text = "What is the matter with you that you make such a judgment?" WHERE id = 5306;
UPDATE quran_ayahs SET en_text = "Or do you have a scripture in which you read" WHERE id = 5307;
UPDATE quran_ayahs SET en_text = "that you will be given whatever you choose?" WHERE id = 5308;
UPDATE quran_ayahs SET en_text = "Or do you have a solemn promise binding upon Us until the Day of Resurrection, that you will have whatever you decide for yourselves?" WHERE id = 5309;
UPDATE quran_ayahs SET en_text = "Ask them which of them can guarantee that." WHERE id = 5310;
UPDATE quran_ayahs SET en_text = "Or do they have associate-gods? Then let them bring forth their associate-gods, if they are truthful." WHERE id = 5311;
UPDATE quran_ayahs SET en_text = "On the Day when the Shin \\[of Allah\\] will be uncovered, and they are invited to prostrate, but they will not be able to do so." WHERE id = 5312;
UPDATE quran_ayahs SET en_text = "Their eyes will be downcast, overwhelmed with humiliation, for they were invited to prostrate when they were capable \\[but they refused\\]." WHERE id = 5313;
UPDATE quran_ayahs SET en_text = "So leave to Me those who reject this message \\[O Prophet\\]. We will gradually lead them to their ruin in ways that they do not perceive." WHERE id = 5314;
UPDATE quran_ayahs SET en_text = "I will give them respite, but My plan is firm." WHERE id = 5315;
UPDATE quran_ayahs SET en_text = "Or are you asking them for a reward, so they find it too burdensome?" WHERE id = 5316;
UPDATE quran_ayahs SET en_text = "Or do they have knowledge of the unseen, so they write it down?" WHERE id = 5317;
UPDATE quran_ayahs SET en_text = "So be patient with the decree of your Lord, and do not be like \\[Jonah\\], the man of the Whale, when he cried out in anguish." WHERE id = 5318;
UPDATE quran_ayahs SET en_text = "Had it not been for the grace that he received from his Lord, he would have surely been thrown onto the barren shore, blameworthy." WHERE id = 5319;
UPDATE quran_ayahs SET en_text = "But his Lord chose him, and made him one of the righteous." WHERE id = 5320;
UPDATE quran_ayahs SET en_text = "The disbelievers would almost strike you down with their malicious gazes when they hear the Reminder, and they say, “He is certainly mad.”" WHERE id = 5321;
UPDATE quran_ayahs SET en_text = "But it is nothing other than a reminder to the whole world." WHERE id = 5322;
UPDATE quran_ayahs SET en_text = "The Inevitable Hour!" WHERE id = 5323;
UPDATE quran_ayahs SET en_text = "What is the Inevitable Hour?" WHERE id = 5324;
UPDATE quran_ayahs SET en_text = "How do you know what the Inevitable Hour is?" WHERE id = 5325;
UPDATE quran_ayahs SET en_text = "Thamūd and ‘Ād denied the striking calamity of the Hour." WHERE id = 5326;
UPDATE quran_ayahs SET en_text = "As for Thamūd, they were destroyed by a dreadful blast." WHERE id = 5327;
UPDATE quran_ayahs SET en_text = "And as for ‘Ād, they were destroyed by a furious windstorm" WHERE id = 5328;
UPDATE quran_ayahs SET en_text = "which He unleashed against them for seven successive nights and eight days, so you would see the people lying dead as if they were hollow trunks of palm trees." WHERE id = 5329;
UPDATE quran_ayahs SET en_text = "Do you see any trace of them now?" WHERE id = 5330;
UPDATE quran_ayahs SET en_text = "Also Pharaoh and those who came before him, and the overturned cities \\[of Lot\\] committed grave sin," WHERE id = 5331;
UPDATE quran_ayahs SET en_text = "each disobeying the messenger of their Lord, so He seized them with a severe punishment." WHERE id = 5332;
UPDATE quran_ayahs SET en_text = "Indeed, when the floodwater overflowed, We carried you \\[O humans\\] in the sailing Ark," WHERE id = 5333;
UPDATE quran_ayahs SET en_text = "so that We might make it a reminder for you, and that attentive ears may take heed." WHERE id = 5334;
UPDATE quran_ayahs SET en_text = "Then when the Trumpet is blown with a single blast," WHERE id = 5335;
UPDATE quran_ayahs SET en_text = "and the earth and mountains are lifted up and crushed with a single blow," WHERE id = 5336;
UPDATE quran_ayahs SET en_text = "on that Day the Inevitable Event will occur." WHERE id = 5337;
UPDATE quran_ayahs SET en_text = "And the sky will split asunder and will be frail on that day," WHERE id = 5338;
UPDATE quran_ayahs SET en_text = "with the angels on all its sides, and on that Day, eight \\[mighty angels\\] will bear the Throne of your Lord above them." WHERE id = 5339;
UPDATE quran_ayahs SET en_text = "On that day, you will be brought forth \\[before Allah\\], and none of your secrets will remain hidden." WHERE id = 5340;
UPDATE quran_ayahs SET en_text = "As for the one who is given his Record in his right hand, he will say, “Here is my Record, read it!" WHERE id = 5341;
UPDATE quran_ayahs SET en_text = "I was sure that I would meet my reckoning.”" WHERE id = 5342;
UPDATE quran_ayahs SET en_text = "So he will be in a pleasant life," WHERE id = 5343;
UPDATE quran_ayahs SET en_text = "in a lofty garden," WHERE id = 5344;
UPDATE quran_ayahs SET en_text = "with clustered fruit within his reach." WHERE id = 5345;
UPDATE quran_ayahs SET en_text = "\\[They will be told,\\] “Eat and drink joyfully for what you did in the days gone by.”" WHERE id = 5346;
UPDATE quran_ayahs SET en_text = "As for the one who is given his Record in his left hand, he will say, “Would that I had not been given my Record," WHERE id = 5347;
UPDATE quran_ayahs SET en_text = "nor had I known anything of my reckoning!" WHERE id = 5348;
UPDATE quran_ayahs SET en_text = "Would that it \\[death\\] had been the end of me!" WHERE id = 5349;
UPDATE quran_ayahs SET en_text = "My wealth has been of no avail to me." WHERE id = 5350;
UPDATE quran_ayahs SET en_text = "My authority has vanished.”" WHERE id = 5351;
UPDATE quran_ayahs SET en_text = "\\[It will be said,\\] “Seize him and shackle him," WHERE id = 5352;
UPDATE quran_ayahs SET en_text = "then make him burn in the Blazing Fire," WHERE id = 5353;
UPDATE quran_ayahs SET en_text = "then tie him up with a chain of seventy cubits long." WHERE id = 5354;
UPDATE quran_ayahs SET en_text = "For he did not believe in Allah, the Most Great," WHERE id = 5355;
UPDATE quran_ayahs SET en_text = "nor did he encourage feeding the needy." WHERE id = 5356;
UPDATE quran_ayahs SET en_text = "So Today he will have no close friend here," WHERE id = 5357;
UPDATE quran_ayahs SET en_text = "nor any food except the discharge of wounds," WHERE id = 5358;
UPDATE quran_ayahs SET en_text = "none will eat it except the sinners.”" WHERE id = 5359;
UPDATE quran_ayahs SET en_text = "I swear by what you can see." WHERE id = 5360;
UPDATE quran_ayahs SET en_text = "and what you cannot see!" WHERE id = 5361;
UPDATE quran_ayahs SET en_text = "Indeed, this is the word of a noble Messenger," WHERE id = 5362;
UPDATE quran_ayahs SET en_text = "and it is not the word of a poet; little do you believe!" WHERE id = 5363;
UPDATE quran_ayahs SET en_text = "Nor is it the word of a soothsayer; little do you take heed!" WHERE id = 5364;
UPDATE quran_ayahs SET en_text = "It is a revelation from the Lord of the worlds." WHERE id = 5365;
UPDATE quran_ayahs SET en_text = "If he had falsely attributed something to Us," WHERE id = 5366;
UPDATE quran_ayahs SET en_text = "We would have surely seized him by the right hand," WHERE id = 5367;
UPDATE quran_ayahs SET en_text = "then severed his aorta," WHERE id = 5368;
UPDATE quran_ayahs SET en_text = "and none of you could have rescued him." WHERE id = 5369;
UPDATE quran_ayahs SET en_text = "This \\[Qur’an\\] is indeed a reminder for those who are righteous." WHERE id = 5370;
UPDATE quran_ayahs SET en_text = "And We surely know that some of you will be deniers," WHERE id = 5371;
UPDATE quran_ayahs SET en_text = "and it will certainly be a source of regret for the disbelievers." WHERE id = 5372;
UPDATE quran_ayahs SET en_text = "And this is indeed the absolute truth." WHERE id = 5373;
UPDATE quran_ayahs SET en_text = "So glorify the name of your Lord, the Most Great." WHERE id = 5374;
UPDATE quran_ayahs SET en_text = "A challenger asked for a punishment bound to come" WHERE id = 5375;
UPDATE quran_ayahs SET en_text = "upon the disbelievers, which none can avert," WHERE id = 5376;
UPDATE quran_ayahs SET en_text = "from Allah, Lord of the pathways of ascent," WHERE id = 5377;
UPDATE quran_ayahs SET en_text = "by which the angels and the Spirit will ascend to Him on a Day the length of which is fifty thousand years." WHERE id = 5378;
UPDATE quran_ayahs SET en_text = "So be patient \\[O Prophet\\] with beautiful patience." WHERE id = 5379;
UPDATE quran_ayahs SET en_text = "Indeed, they see this \\[punishment\\] far off," WHERE id = 5380;
UPDATE quran_ayahs SET en_text = "but We see it to be close at hand." WHERE id = 5381;
UPDATE quran_ayahs SET en_text = "On the Day when the sky will be like molten brass" WHERE id = 5382;
UPDATE quran_ayahs SET en_text = "and the mountains will be like dyed wool," WHERE id = 5383;
UPDATE quran_ayahs SET en_text = "and no close friend will ask about another," WHERE id = 5384;
UPDATE quran_ayahs SET en_text = "even though they will see one another. The wicked will wish he could ransom himself from the punishment of that Day by offering his children," WHERE id = 5385;
UPDATE quran_ayahs SET en_text = "and his wife and his brother," WHERE id = 5386;
UPDATE quran_ayahs SET en_text = "and his kindred who stood by him," WHERE id = 5387;
UPDATE quran_ayahs SET en_text = "and everyone on earth, just to save himself." WHERE id = 5388;
UPDATE quran_ayahs SET en_text = "By no means! It is a raging Flame," WHERE id = 5389;
UPDATE quran_ayahs SET en_text = "that will strip off the scalps." WHERE id = 5390;
UPDATE quran_ayahs SET en_text = "It will call everyone who turned their backs and disobeyed," WHERE id = 5391;
UPDATE quran_ayahs SET en_text = "and amassed and hoarded wealth." WHERE id = 5392;
UPDATE quran_ayahs SET en_text = "Indeed, man was created impatient:" WHERE id = 5393;
UPDATE quran_ayahs SET en_text = "when evil befalls him, he bewails," WHERE id = 5394;
UPDATE quran_ayahs SET en_text = "but when good fortune comes his way, he becomes tight-fisted –" WHERE id = 5395;
UPDATE quran_ayahs SET en_text = "except those who pray," WHERE id = 5396;
UPDATE quran_ayahs SET en_text = "who are steadfast in their prayers;" WHERE id = 5397;
UPDATE quran_ayahs SET en_text = "and who give a due share of their wealth" WHERE id = 5398;
UPDATE quran_ayahs SET en_text = "to the beggar and the dispossessed;" WHERE id = 5399;
UPDATE quran_ayahs SET en_text = "and who firmly believe in the Day of Judgment;" WHERE id = 5400;
UPDATE quran_ayahs SET en_text = "and those who fear the punishment of their Lord –" WHERE id = 5401;
UPDATE quran_ayahs SET en_text = "for none can feel secure from their Lord’s punishment –" WHERE id = 5402;
UPDATE quran_ayahs SET en_text = "and those who guard their private parts," WHERE id = 5403;
UPDATE quran_ayahs SET en_text = "except from their wives or bondwomen whom they own, for then they are not to be blamed." WHERE id = 5404;
UPDATE quran_ayahs SET en_text = "But whoever seeks beyond that, it is they who are the transgressors." WHERE id = 5405;
UPDATE quran_ayahs SET en_text = "and those who fulfill their trusts and pledges;" WHERE id = 5406;
UPDATE quran_ayahs SET en_text = "and who are upright in their testimonies;" WHERE id = 5407;
UPDATE quran_ayahs SET en_text = "and who take due care of their prayers;" WHERE id = 5408;
UPDATE quran_ayahs SET en_text = "they will be honored in gardens." WHERE id = 5409;
UPDATE quran_ayahs SET en_text = "What is the matter with the disbelievers that they are rushing towards you \\[O Prophet\\]," WHERE id = 5410;
UPDATE quran_ayahs SET en_text = "from the right and from the left, in crowds?" WHERE id = 5411;
UPDATE quran_ayahs SET en_text = "Does everyone of them aspire to enter a garden of bliss?" WHERE id = 5412;
UPDATE quran_ayahs SET en_text = "By no means! We have created them from what they know." WHERE id = 5413;
UPDATE quran_ayahs SET en_text = "I swear by the Lord of the sunrises and sunsets that We are surely Capable" WHERE id = 5414;
UPDATE quran_ayahs SET en_text = "to replace them with others better than them, and none can escape Our decree." WHERE id = 5415;
UPDATE quran_ayahs SET en_text = "So leave them to engage in their falsehood and amuse themselves until they meet their Day which they are promised;" WHERE id = 5416;
UPDATE quran_ayahs SET en_text = "the Day when they will rush out of their graves as if they are racing towards a goal," WHERE id = 5417;
UPDATE quran_ayahs SET en_text = "with their eyes downcast and overwhelmed with disgrace. That is the Day that they were promised." WHERE id = 5418;
UPDATE quran_ayahs SET en_text = "Indeed, We sent Noah to his people, “Warn your people before there comes to them a painful punishment.”" WHERE id = 5419;
UPDATE quran_ayahs SET en_text = "He said: “O my people, indeed I am a clear warner to you," WHERE id = 5420;
UPDATE quran_ayahs SET en_text = "that you worship Allah and fear Him, and obey me;" WHERE id = 5421;
UPDATE quran_ayahs SET en_text = "He will forgive you some of your sins and grant you respite until an appointed term. Indeed, when Allah’s appointed term comes, it cannot be delayed, if you only knew!”" WHERE id = 5422;
UPDATE quran_ayahs SET en_text = "He said, “My Lord, I have surely called my people night and day," WHERE id = 5423;
UPDATE quran_ayahs SET en_text = "but my call only drove them further away." WHERE id = 5424;
UPDATE quran_ayahs SET en_text = "Every time I call them so that You may forgive them, they thrust their fingers into their ears and cover their faces with their garments, and persist in obstinacy and grow extremely arrogant." WHERE id = 5425;
UPDATE quran_ayahs SET en_text = "Then I called them openly," WHERE id = 5426;
UPDATE quran_ayahs SET en_text = "then I addressed them in public and in private," WHERE id = 5427;
UPDATE quran_ayahs SET en_text = "I said, ‘Seek forgiveness from your Lord. Indeed, He is Most Forgiving." WHERE id = 5428;
UPDATE quran_ayahs SET en_text = "He will shower you with abundant rain from the sky," WHERE id = 5429;
UPDATE quran_ayahs SET en_text = "and He will give you wealth and children, and bestow upon you gardens and rivers." WHERE id = 5430;
UPDATE quran_ayahs SET en_text = "What is the matter with you that you do not fear the Majesty of Allah," WHERE id = 5431;
UPDATE quran_ayahs SET en_text = "when He has created you in stages?" WHERE id = 5432;
UPDATE quran_ayahs SET en_text = "Do you not see how Allah has created seven heavens, one above another," WHERE id = 5433;
UPDATE quran_ayahs SET en_text = "and has placed the moon therein as a light and the sun as a burning lamp?" WHERE id = 5434;
UPDATE quran_ayahs SET en_text = "Allah has produced you from the earth like a plant," WHERE id = 5435;
UPDATE quran_ayahs SET en_text = "then He will return you into it, and then will bring you out again." WHERE id = 5436;
UPDATE quran_ayahs SET en_text = "Allah has made the earth wide spread for you" WHERE id = 5437;
UPDATE quran_ayahs SET en_text = "so that you may tread its spacious paths.’”" WHERE id = 5438;
UPDATE quran_ayahs SET en_text = "Noah said, “My Lord, they have disobeyed me and followed those whose wealth and children did not increase them except in loss." WHERE id = 5439;
UPDATE quran_ayahs SET en_text = "They have contrived a mighty plot," WHERE id = 5440;
UPDATE quran_ayahs SET en_text = "They said, ‘Do not abandon your gods; and do not abandon Wadd, Suwā‘, Yaghūth, Ya‘ūq, and Nasr.’" WHERE id = 5441;
UPDATE quran_ayahs SET en_text = "They have led many astray. So \\[O Lord\\], do not increase the wrongdoers except in error.”" WHERE id = 5442;
UPDATE quran_ayahs SET en_text = "Because of their sins, they were drowned and then were cast into the Fire, and they did not find any helpers against Allah." WHERE id = 5443;
UPDATE quran_ayahs SET en_text = "Noah said, “My Lord, do not leave on earth a single disbeliever." WHERE id = 5444;
UPDATE quran_ayahs SET en_text = "For if You leave them, they will surely lead Your slaves astray and beget none but wicked disbelievers." WHERE id = 5445;
UPDATE quran_ayahs SET en_text = "My Lord, forgive me and my parents, and whoever enters my house as a believer, and all the believing men and women, and do not increase the wrongdoers except in ruin.”" WHERE id = 5446;
UPDATE quran_ayahs SET en_text = "Say, “It has been revealed to me that a group of jinn listened \\[to the Qur’an,\\] and they said, ‘Indeed, we have heard a wondrous recitation" WHERE id = 5447;
UPDATE quran_ayahs SET en_text = "that guides to the right way, so we have believed in it, and we will never associate anyone with our Lord." WHERE id = 5448;
UPDATE quran_ayahs SET en_text = "And that He – our exalted and glorified Lord – has neither taken a wife nor a child;" WHERE id = 5449;
UPDATE quran_ayahs SET en_text = "and that the fool among us \\[Satan\\] used to say outrageous things about Allah," WHERE id = 5450;
UPDATE quran_ayahs SET en_text = "although we thought that humans and jinn would never tell lies about Allah." WHERE id = 5451;
UPDATE quran_ayahs SET en_text = "And there were some men who used to seek refuge with some jinn, but they only increased them in burden." WHERE id = 5452;
UPDATE quran_ayahs SET en_text = "They thought, just like you \\[jinn\\] thought that Allah would never send any messenger." WHERE id = 5453;
UPDATE quran_ayahs SET en_text = "We have sought to reach the heaven but found it filled with stern guards and flaming fire." WHERE id = 5454;
UPDATE quran_ayahs SET en_text = "We used to take up positions there for eavesdropping, but now anyone who eavesdrops will find a flaming fire waiting for him." WHERE id = 5455;
UPDATE quran_ayahs SET en_text = "We do not know whether evil is intended for those on earth, or their Lord intends good for them." WHERE id = 5456;
UPDATE quran_ayahs SET en_text = "Among us are some who are righteous and some who are otherwise, for we follow divergent ways." WHERE id = 5457;
UPDATE quran_ayahs SET en_text = "We have realized that we can never escape Allah on earth, nor can we ever escape Him by fleeing." WHERE id = 5458;
UPDATE quran_ayahs SET en_text = "When we heard the guidance \\[of the Qur’an\\], we believed in it. Whoever believes in his Lord will have no fear of curtailment \\[from his reward\\] or injustice." WHERE id = 5459;
UPDATE quran_ayahs SET en_text = "Among us are some who are Muslims and some who are deviant. Those who accepted Islam have sought the true guidance," WHERE id = 5460;
UPDATE quran_ayahs SET en_text = "but those who are deviant, they will be fuel for Hell.’”" WHERE id = 5461;
UPDATE quran_ayahs SET en_text = "“If they had remained steadfast on the right way, We would have given them abundant rain," WHERE id = 5462;
UPDATE quran_ayahs SET en_text = "as a test for them. But whoever turns away from the remembrance of his Lord, He will make him suffer an arduous punishment." WHERE id = 5463;
UPDATE quran_ayahs SET en_text = "The mosques are for Allah alone, so do not supplicate to anyone along with Allah." WHERE id = 5464;
UPDATE quran_ayahs SET en_text = "Yet when the slave of Allah stood up supplicating to Him, they swarmed around him.”" WHERE id = 5465;
UPDATE quran_ayahs SET en_text = "Say \\[O Prophet\\], “I only supplicate to my Lord and I do not associate anyone with Him.”" WHERE id = 5466;
UPDATE quran_ayahs SET en_text = "Say, “I have no power to harm or benefit you.”" WHERE id = 5467;
UPDATE quran_ayahs SET en_text = "Say, “None can ever protect me from Allah \\[if I should disobey Him\\], nor can I ever find refuge except in Him." WHERE id = 5468;
UPDATE quran_ayahs SET en_text = "I only convey what I receive from Allah and His messages.” And whoever disobeys Allah and His Messenger will certainly be in the Fire of Hell, abiding therein forever." WHERE id = 5469;
UPDATE quran_ayahs SET en_text = "Until when they see what they were promised, they will realize who is weaker in helpers and fewer in number." WHERE id = 5470;
UPDATE quran_ayahs SET en_text = "Say, “I do not know if what you are promised is near or my Lord has appointed a distant time for it." WHERE id = 5471;
UPDATE quran_ayahs SET en_text = "\\[He is\\] the Knower of the unseen, He does not reveal His unseen to anyone," WHERE id = 5472;
UPDATE quran_ayahs SET en_text = "except the messenger whom He chooses, then He appoints angel-guards before him and behind him" WHERE id = 5473;
UPDATE quran_ayahs SET en_text = "to ensure that the messengers fully conveyed the messages of their Lord. He encompasses in His knowledge all about them, and keeps count of all things.”" WHERE id = 5474;
UPDATE quran_ayahs SET en_text = "O you the enwrapped one," WHERE id = 5475;
UPDATE quran_ayahs SET en_text = "Stand up in prayer at night except a little," WHERE id = 5476;
UPDATE quran_ayahs SET en_text = "half of it, or a little less," WHERE id = 5477;
UPDATE quran_ayahs SET en_text = "or a little more, and recite the Qur’an at a measured pace." WHERE id = 5478;
UPDATE quran_ayahs SET en_text = "We will send down upon you a heavy revelation." WHERE id = 5479;
UPDATE quran_ayahs SET en_text = "Indeed, prayer at night is more effective and more conducive to proper recitation." WHERE id = 5480;
UPDATE quran_ayahs SET en_text = "In the daytime, you have plenty of time for your worldly matters." WHERE id = 5481;
UPDATE quran_ayahs SET en_text = "And remember the Name of your Lord, and devote yourself completely to Him." WHERE id = 5482;
UPDATE quran_ayahs SET en_text = "\\[He is\\] Lord of the east and the west; none has the right to be worshiped except Him, so take Him as the Disposer of your affairs." WHERE id = 5483;
UPDATE quran_ayahs SET en_text = "Bear patiently what they say, and avoid them in a gracious manner." WHERE id = 5484;
UPDATE quran_ayahs SET en_text = "Leave to Me the deniers who are having a life of luxury, and bear with them for a little while." WHERE id = 5485;
UPDATE quran_ayahs SET en_text = "Indeed, We have heavy shackles and a Blazing Fire," WHERE id = 5486;
UPDATE quran_ayahs SET en_text = "and food that chokes, and a painful punishment," WHERE id = 5487;
UPDATE quran_ayahs SET en_text = "on the Day when the earth and the mountains will convulse, and the mountains will be like dunes of loose sand." WHERE id = 5488;
UPDATE quran_ayahs SET en_text = "Indeed, We have sent to you a Messenger to be a witness over you, just as We sent to Pharaoh a messenger." WHERE id = 5489;
UPDATE quran_ayahs SET en_text = "But Pharaoh disobeyed the messenger, so We seized him with a severe punishment." WHERE id = 5490;
UPDATE quran_ayahs SET en_text = "If you disbelieve, then how will you protect yourselves against a Day that will turn children’s hair gray?" WHERE id = 5491;
UPDATE quran_ayahs SET en_text = "It will cause the sky to break asunder. His promise will certainly be fulfilled." WHERE id = 5492;
UPDATE quran_ayahs SET en_text = "Indeed, this is a reminder; so whoever wills may take a way to his Lord." WHERE id = 5493;
UPDATE quran_ayahs SET en_text = "Indeed, your Lord knows that you \\[O Prophet\\] stand up in prayer for nearly two-thirds of the night, or half of it, or one-third of it, as do others among your companions. Allah determines the night and the day; He knows that you \\[Muslims\\] cannot keep an accurate count of it, so He pardoned you. Recite then as much of the Qur’an as is easy for you \\[in the night prayers\\]. He knows that there are some among you who will be ill, and others traveling in the land, seeking the grace of Allah, and others fighting in Allah’s way. So recite as much of it as is easy for you; establish prayer and give zakah; and lend to Allah a goodly loan. Whatever good you send forth for yourselves, you will find it with Allah, much better in condition and much greater in reward. And seek forgiveness of Allah, for indeed Allah is All-Forgiving, Most-Merciful." WHERE id = 5494;
UPDATE quran_ayahs SET en_text = "O you covered \\[in your cloak\\]," WHERE id = 5495;
UPDATE quran_ayahs SET en_text = "arise and warn," WHERE id = 5496;
UPDATE quran_ayahs SET en_text = "and proclaim the greatness of your Lord," WHERE id = 5497;
UPDATE quran_ayahs SET en_text = "and purify your garments," WHERE id = 5498;
UPDATE quran_ayahs SET en_text = "and shun idol worshiping," WHERE id = 5499;
UPDATE quran_ayahs SET en_text = "and do not do a favor for a greater return," WHERE id = 5500;
UPDATE quran_ayahs SET en_text = "and be patient for the sake of your Lord." WHERE id = 5501;
UPDATE quran_ayahs SET en_text = "When the Trumpet is sounded," WHERE id = 5502;
UPDATE quran_ayahs SET en_text = "that Day will be a difficult day," WHERE id = 5503;
UPDATE quran_ayahs SET en_text = "not easy for the disbelievers." WHERE id = 5504;
UPDATE quran_ayahs SET en_text = "Leave me to deal with the one whom I created lonely \\[having no wealth or children etc\\]," WHERE id = 5505;
UPDATE quran_ayahs SET en_text = "and gave him abundant wealth," WHERE id = 5506;
UPDATE quran_ayahs SET en_text = "and sons by his side," WHERE id = 5507;
UPDATE quran_ayahs SET en_text = "and gave him a life of comfort and power," WHERE id = 5508;
UPDATE quran_ayahs SET en_text = "yet he aspires that I should give him more." WHERE id = 5509;
UPDATE quran_ayahs SET en_text = "By no means! For he has been stubborn against Our verses." WHERE id = 5510;
UPDATE quran_ayahs SET en_text = "I will subject him to an unbearable punishment." WHERE id = 5511;
UPDATE quran_ayahs SET en_text = "For he reflected and plotted \\[against the Qur’an\\]." WHERE id = 5512;
UPDATE quran_ayahs SET en_text = "May he perish, how he plotted!" WHERE id = 5513;
UPDATE quran_ayahs SET en_text = "Again, may he perish, how he plotted!" WHERE id = 5514;
UPDATE quran_ayahs SET en_text = "Then he looked around," WHERE id = 5515;
UPDATE quran_ayahs SET en_text = "then he frowned and scowled," WHERE id = 5516;
UPDATE quran_ayahs SET en_text = "then turned away with arrogance," WHERE id = 5517;
UPDATE quran_ayahs SET en_text = "then said, “This is nothing but magic from the past." WHERE id = 5518;
UPDATE quran_ayahs SET en_text = "This is nothing but the words of a man.”" WHERE id = 5519;
UPDATE quran_ayahs SET en_text = "Soon I will make him burn in the Scorching Fire." WHERE id = 5520;
UPDATE quran_ayahs SET en_text = "How do you know what Scorching Fire is?" WHERE id = 5521;
UPDATE quran_ayahs SET en_text = "It leaves nothing and spares no one," WHERE id = 5522;
UPDATE quran_ayahs SET en_text = "scorching the skin." WHERE id = 5523;
UPDATE quran_ayahs SET en_text = "It is overseen by nineteen \\[angels\\]." WHERE id = 5524;
UPDATE quran_ayahs SET en_text = "We have only appointed angels as keepers of the Fire, and We have only made their number as a test for the disbelievers, so that those who were given the Scripture may have certainty and the believers may increase in faith; and neither those who were given the Scripture nor the believers may have any doubts; and so that those \\[hypocrites\\] in whose hearts is sickness and the disbelievers may say, “What does Allah mean by this number?” Thus Allah causes to stray whom He wills and guides whom He wills, and none knows the soldiers of your Lord but He. This is but a reminder to mankind." WHERE id = 5525;
UPDATE quran_ayahs SET en_text = "No indeed! By the moon," WHERE id = 5526;
UPDATE quran_ayahs SET en_text = "and by the night when it departs," WHERE id = 5527;
UPDATE quran_ayahs SET en_text = "and by the dawn when it brightens," WHERE id = 5528;
UPDATE quran_ayahs SET en_text = "Hellfire is indeed one of the greatest matters," WHERE id = 5529;
UPDATE quran_ayahs SET en_text = "a warning to mankind," WHERE id = 5530;
UPDATE quran_ayahs SET en_text = "to whoever among you chooses to advance or regress." WHERE id = 5531;
UPDATE quran_ayahs SET en_text = "Every soul is held in pledge for its deeds," WHERE id = 5532;
UPDATE quran_ayahs SET en_text = "except the People of the Right," WHERE id = 5533;
UPDATE quran_ayahs SET en_text = "who will be in gardens, asking one another" WHERE id = 5534;
UPDATE quran_ayahs SET en_text = "about the wicked:" WHERE id = 5535;
UPDATE quran_ayahs SET en_text = "“What brought you to Hell?”" WHERE id = 5536;
UPDATE quran_ayahs SET en_text = "They will say, “We were not among those who prayed," WHERE id = 5537;
UPDATE quran_ayahs SET en_text = "nor did we feed the poor," WHERE id = 5538;
UPDATE quran_ayahs SET en_text = "and we indulged in falsehood along with those who indulged in it," WHERE id = 5539;
UPDATE quran_ayahs SET en_text = "and we denied the Day of Judgment," WHERE id = 5540;
UPDATE quran_ayahs SET en_text = "until the certainty \\[of death\\] came to us.”" WHERE id = 5541;
UPDATE quran_ayahs SET en_text = "So the intercession of intercessors will not benefit them." WHERE id = 5542;
UPDATE quran_ayahs SET en_text = "Then what is the matter with them that they turn away from the Reminder," WHERE id = 5543;
UPDATE quran_ayahs SET en_text = "as if they were terrified wild asses," WHERE id = 5544;
UPDATE quran_ayahs SET en_text = "fleeing from a lion?" WHERE id = 5545;
UPDATE quran_ayahs SET en_text = "In fact, each one of them desires that open \\[divine\\] letters be sent to each of them." WHERE id = 5546;
UPDATE quran_ayahs SET en_text = "By no means! They rather do not fear the Hereafter." WHERE id = 5547;
UPDATE quran_ayahs SET en_text = "No indeed! This \\[Qur’an\\] is a reminder." WHERE id = 5548;
UPDATE quran_ayahs SET en_text = "So whoever wills may take heed." WHERE id = 5549;
UPDATE quran_ayahs SET en_text = "But they will not take heed unless Allah so wills. He is most worthy to be feared and most worthy to forgive." WHERE id = 5550;
UPDATE quran_ayahs SET en_text = "I swear by the Day of Resurrection," WHERE id = 5551;
UPDATE quran_ayahs SET en_text = "and I swear by the self-reproaching soul!" WHERE id = 5552;
UPDATE quran_ayahs SET en_text = "Does man think that We cannot reassemble his bones?" WHERE id = 5553;
UPDATE quran_ayahs SET en_text = "In fact, We are able to restore even his very fingertips." WHERE id = 5554;
UPDATE quran_ayahs SET en_text = "But man desires to persist in his evil ways," WHERE id = 5555;
UPDATE quran_ayahs SET en_text = "and asks \\[mockingly\\], “When is the Day of Resurrection?”" WHERE id = 5556;
UPDATE quran_ayahs SET en_text = "But when eyes are dazzled," WHERE id = 5557;
UPDATE quran_ayahs SET en_text = "and the moon is darkened," WHERE id = 5558;
UPDATE quran_ayahs SET en_text = "and the sun and the moon are brought together," WHERE id = 5559;
UPDATE quran_ayahs SET en_text = "on that Day man will say, “Where is the escape?”" WHERE id = 5560;
UPDATE quran_ayahs SET en_text = "No indeed! There will be no refuge." WHERE id = 5561;
UPDATE quran_ayahs SET en_text = "On that Day all will end up before your Lord." WHERE id = 5562;
UPDATE quran_ayahs SET en_text = "On that Day man will be informed of what he has sent forth and left behind." WHERE id = 5563;
UPDATE quran_ayahs SET en_text = "In fact, man will be a witness against himself," WHERE id = 5564;
UPDATE quran_ayahs SET en_text = "despite the excuses he may put forward." WHERE id = 5565;
UPDATE quran_ayahs SET en_text = "Do not move your tongue \\[O Prophet\\] in haste trying to memorize it." WHERE id = 5566;
UPDATE quran_ayahs SET en_text = "It is upon Us to make you memorize and recite it." WHERE id = 5567;
UPDATE quran_ayahs SET en_text = "Then when We recite it \\[through Gabriel\\], follow its recitation attentively;" WHERE id = 5568;
UPDATE quran_ayahs SET en_text = "then it is upon Us to explain its meanings." WHERE id = 5569;
UPDATE quran_ayahs SET en_text = "No indeed! You \\[people\\] love this fleeting world," WHERE id = 5570;
UPDATE quran_ayahs SET en_text = "and neglect the Hereafter." WHERE id = 5571;
UPDATE quran_ayahs SET en_text = "On that Day some faces will be bright," WHERE id = 5572;
UPDATE quran_ayahs SET en_text = "looking at their Lord." WHERE id = 5573;
UPDATE quran_ayahs SET en_text = "And on that Day, some faces will be gloomy," WHERE id = 5574;
UPDATE quran_ayahs SET en_text = "knowing that a crushing calamity will befall them." WHERE id = 5575;
UPDATE quran_ayahs SET en_text = "Indeed, when the soul reaches the throat," WHERE id = 5576;
UPDATE quran_ayahs SET en_text = "and it is said, “Is there any healer?”" WHERE id = 5577;
UPDATE quran_ayahs SET en_text = "And he realizes that it is time to depart," WHERE id = 5578;
UPDATE quran_ayahs SET en_text = "when legs are brought together \\[in a shroud\\];" WHERE id = 5579;
UPDATE quran_ayahs SET en_text = "on that Day, he will be driven to your Lord." WHERE id = 5580;
UPDATE quran_ayahs SET en_text = "He neither believed nor prayed;" WHERE id = 5581;
UPDATE quran_ayahs SET en_text = "rather he denied and turned away," WHERE id = 5582;
UPDATE quran_ayahs SET en_text = "then went swaggering to his people," WHERE id = 5583;
UPDATE quran_ayahs SET en_text = "Woe to you \\[O man\\], and woe!" WHERE id = 5584;
UPDATE quran_ayahs SET en_text = "Again, woe to you, and woe!" WHERE id = 5585;
UPDATE quran_ayahs SET en_text = "Does man think that he will be left neglected?" WHERE id = 5586;
UPDATE quran_ayahs SET en_text = "Was he not once a drop of semen emitted," WHERE id = 5587;
UPDATE quran_ayahs SET en_text = "then he became a clinging clot, then He created him and perfected his form," WHERE id = 5588;
UPDATE quran_ayahs SET en_text = "and made from him both genders, male and female?" WHERE id = 5589;
UPDATE quran_ayahs SET en_text = "Is not He able to bring the dead back to life?" WHERE id = 5590;
UPDATE quran_ayahs SET en_text = "Was there not a period of time when man was not a thing \\[even\\] mentioned?" WHERE id = 5591;
UPDATE quran_ayahs SET en_text = "Indeed, We created man from a drop of mixed fluids, in order to test him. So We gave him hearing and sight." WHERE id = 5592;
UPDATE quran_ayahs SET en_text = "Indeed, We showed him the way, whether he is grateful or ungrateful." WHERE id = 5593;
UPDATE quran_ayahs SET en_text = "We have surely prepared for the disbelievers chains, shackles, and a Blazing Fire." WHERE id = 5594;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will drink from a cup \\[of wine\\] flavored with camphor," WHERE id = 5595;
UPDATE quran_ayahs SET en_text = "a spring from which Allah’s slaves will drink, which will flow as they wish." WHERE id = 5596;
UPDATE quran_ayahs SET en_text = "They fulfill their vows and fear a Day whose evil will spread far and wide," WHERE id = 5597;
UPDATE quran_ayahs SET en_text = "and they give food, despite their love for it, to the needy, the orphans, and the captives," WHERE id = 5598;
UPDATE quran_ayahs SET en_text = "\\[saying to themselves\\] “We feed you only for the sake of Allah; we seek from you neither reward nor gratitude." WHERE id = 5599;
UPDATE quran_ayahs SET en_text = "We fear from our Lord a grim and distressful Day.”" WHERE id = 5600;
UPDATE quran_ayahs SET en_text = "So Allah will protect them from the evil of that Day, and will grant them radiance and delight," WHERE id = 5601;
UPDATE quran_ayahs SET en_text = "and will reward them for their perseverance with Paradise and garments of silk." WHERE id = 5602;
UPDATE quran_ayahs SET en_text = "They will recline therein on adorned couches; neither feeling scorching heat nor freezing cold." WHERE id = 5603;
UPDATE quran_ayahs SET en_text = "with shady branches spread above them, and clusters of fruit will be made within their reach." WHERE id = 5604;
UPDATE quran_ayahs SET en_text = "They will be served frequently with silver vessels and crystal cups," WHERE id = 5605;
UPDATE quran_ayahs SET en_text = "crystalline goblets of silver, filled precisely as desired." WHERE id = 5606;
UPDATE quran_ayahs SET en_text = "They will be given a glass \\[of wine\\] flavored with ginger" WHERE id = 5607;
UPDATE quran_ayahs SET en_text = "from a spring therein called Salsabīl." WHERE id = 5608;
UPDATE quran_ayahs SET en_text = "They will be served by eternal young boys; if you see them, you would think that they are scattered pearls." WHERE id = 5609;
UPDATE quran_ayahs SET en_text = "If you were to look around, you would see bliss and a vast dominion." WHERE id = 5610;
UPDATE quran_ayahs SET en_text = "They will be dressed in garments of fine green silk and rich brocade, and will be adorned with bracelets of silver, and their Lord will give them a pure drink." WHERE id = 5611;
UPDATE quran_ayahs SET en_text = "\\[And they will be told,\\] “Indeed, this is your reward, and your efforts have been appreciated.”" WHERE id = 5612;
UPDATE quran_ayahs SET en_text = "Indeed, it is We Who have sent down the Qur’an to you in stages." WHERE id = 5613;
UPDATE quran_ayahs SET en_text = "So be patient with your Lord’s decree, and do not obey any sinner or disbeliever from among them." WHERE id = 5614;
UPDATE quran_ayahs SET en_text = "And remember the Name of your Lord morning and evening," WHERE id = 5615;
UPDATE quran_ayahs SET en_text = "and prostrate to Him at night, and glorify Him at length by night." WHERE id = 5616;
UPDATE quran_ayahs SET en_text = "Indeed, these \\[pagans\\] love this fleeting life and pay no heed to a heavy Day ahead of them." WHERE id = 5617;
UPDATE quran_ayahs SET en_text = "It is We Who created them and strengthened their build. If We will, We can replace them with others like them." WHERE id = 5618;
UPDATE quran_ayahs SET en_text = "Indeed, this is a reminder; so let anyone who wills may take a way to his Lord." WHERE id = 5619;
UPDATE quran_ayahs SET en_text = "But you cannot will unless Allah wills. Indeed, Allah is All-Knowing, All-Wise." WHERE id = 5620;
UPDATE quran_ayahs SET en_text = "He admits whom He wills into His mercy, but for the wrongdoers He has prepared a painful punishment." WHERE id = 5621;
UPDATE quran_ayahs SET en_text = "By those \\[winds\\] sent forth in succession," WHERE id = 5622;
UPDATE quran_ayahs SET en_text = "those that blow violently," WHERE id = 5623;
UPDATE quran_ayahs SET en_text = "and scatter \\[rainclouds\\] far and wide," WHERE id = 5624;
UPDATE quran_ayahs SET en_text = "And by those \\[angels\\] who bring criterion;" WHERE id = 5625;
UPDATE quran_ayahs SET en_text = "and those who deliver revelation," WHERE id = 5626;
UPDATE quran_ayahs SET en_text = "leaving no excuse or giving warning –" WHERE id = 5627;
UPDATE quran_ayahs SET en_text = "what you are promised will surely come to pass." WHERE id = 5628;
UPDATE quran_ayahs SET en_text = "So when the stars are extinguished," WHERE id = 5629;
UPDATE quran_ayahs SET en_text = "and when the sky is split apart," WHERE id = 5630;
UPDATE quran_ayahs SET en_text = "and when the mountains are blown away," WHERE id = 5631;
UPDATE quran_ayahs SET en_text = "and when the messengers are summoned," WHERE id = 5632;
UPDATE quran_ayahs SET en_text = "until which Day has all this been postponed?" WHERE id = 5633;
UPDATE quran_ayahs SET en_text = "Until the Day of Judgment." WHERE id = 5634;
