UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5636;
UPDATE quran_ayahs SET en_text = "Did We not destroy the earlier nations?" WHERE id = 5637;
UPDATE quran_ayahs SET en_text = "Then We will do likewise to the later ones." WHERE id = 5638;
UPDATE quran_ayahs SET en_text = "This is how We deal with the wicked." WHERE id = 5639;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5640;
UPDATE quran_ayahs SET en_text = "Did We not create you from a worthless fluid," WHERE id = 5641;
UPDATE quran_ayahs SET en_text = "then We placed it in a secure repository" WHERE id = 5642;
UPDATE quran_ayahs SET en_text = "for a determined period?" WHERE id = 5643;
UPDATE quran_ayahs SET en_text = "For We determined it; How excellently We determine!" WHERE id = 5644;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5645;
UPDATE quran_ayahs SET en_text = "Have We not made the earth a receptacle" WHERE id = 5646;
UPDATE quran_ayahs SET en_text = "for the living and the dead," WHERE id = 5647;
UPDATE quran_ayahs SET en_text = "and placed therein firm towering mountains, and given you fresh water to drink?" WHERE id = 5648;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5649;
UPDATE quran_ayahs SET en_text = "\\[They will be told,\\] “Proceed to that \\[Fire\\] which you used to deny!" WHERE id = 5650;
UPDATE quran_ayahs SET en_text = "Proceed to the shadow \\[of smoke\\] ascending in three columns," WHERE id = 5651;
UPDATE quran_ayahs SET en_text = "that provides neither shade nor protection from the flames." WHERE id = 5652;
UPDATE quran_ayahs SET en_text = "Indeed, it \\[Hell\\] throws up sparks as big as castles," WHERE id = 5653;
UPDATE quran_ayahs SET en_text = "as if they were tawny camels." WHERE id = 5654;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5655;
UPDATE quran_ayahs SET en_text = "On that Day they won’t speak," WHERE id = 5656;
