UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5658;
UPDATE quran_ayahs SET en_text = "\\[They will be told,\\] “This is the Day of Judgment: We have gathered you along with the earlier generations." WHERE id = 5659;
UPDATE quran_ayahs SET en_text = "So if you have any scheme, then plot it against Me.”" WHERE id = 5660;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5661;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will be amid shades and springs" WHERE id = 5662;
UPDATE quran_ayahs SET en_text = "and will have whatever fruit they desire." WHERE id = 5663;
UPDATE quran_ayahs SET en_text = "\\[They will be told,\\] “Eat and drink joyfully, as a reward for what you used to do.”" WHERE id = 5664;
UPDATE quran_ayahs SET en_text = "This is how We reward those who do good." WHERE id = 5665;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5666;
UPDATE quran_ayahs SET en_text = "“Eat and enjoy yourselves for a while; you are truly wicked.”" WHERE id = 5667;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5668;
UPDATE quran_ayahs SET en_text = "When it is said to them, “Bow \\[in prayer\\]”, they do not bow." WHERE id = 5669;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers!" WHERE id = 5670;
UPDATE quran_ayahs SET en_text = "In which message after this \\[Qur’an\\] will they believe?" WHERE id = 5671;
UPDATE quran_ayahs SET en_text = "About what are they asking one another?" WHERE id = 5672;
UPDATE quran_ayahs SET en_text = "About the momentous news," WHERE id = 5673;
UPDATE quran_ayahs SET en_text = "about which they differ." WHERE id = 5674;
UPDATE quran_ayahs SET en_text = "No indeed; they will come to know." WHERE id = 5675;
UPDATE quran_ayahs SET en_text = "Again no; they will come to know." WHERE id = 5676;
UPDATE quran_ayahs SET en_text = "Have We not made the earth a resting place?" WHERE id = 5677;
UPDATE quran_ayahs SET en_text = "And the mountains as stakes," WHERE id = 5678;
UPDATE quran_ayahs SET en_text = "and created you in pairs," WHERE id = 5679;
UPDATE quran_ayahs SET en_text = "and made your sleep for rest," WHERE id = 5680;
UPDATE quran_ayahs SET en_text = "and made the night a covering," WHERE id = 5681;
UPDATE quran_ayahs SET en_text = "and made the day for seeking livelihood," WHERE id = 5682;
