UPDATE quran_ayahs SET en_text = "so that We may produce thereby grains and vegetation," WHERE id = 5686;
UPDATE quran_ayahs SET en_text = "and gardens with dense foliage?" WHERE id = 5687;
UPDATE quran_ayahs SET en_text = "Indeed, the Day of Judgment is a time appointed," WHERE id = 5688;
UPDATE quran_ayahs SET en_text = "the Day when the Trumpet will be blown, you will come forth in crowds," WHERE id = 5689;
