UPDATE quran_ayahs SET en_text = "except scalding water and discharge of wounds," WHERE id = 5696;
UPDATE quran_ayahs SET en_text = "a fitting recompense." WHERE id = 5697;
UPDATE quran_ayahs SET en_text = "Indeed, they did not expect a reckoning," WHERE id = 5698;
UPDATE quran_ayahs SET en_text = "and utterly rejected Our verses." WHERE id = 5699;
UPDATE quran_ayahs SET en_text = "But We have enumerated everything in a record." WHERE id = 5700;
UPDATE quran_ayahs SET en_text = "So taste \\[the punishment\\], for We will not increase you except in torment.”" WHERE id = 5701;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will have salvation," WHERE id = 5702;
UPDATE quran_ayahs SET en_text = "gardens and vineyards," WHERE id = 5703;
UPDATE quran_ayahs SET en_text = "and full-bosomed maidens of equal age," WHERE id = 5704;
UPDATE quran_ayahs SET en_text = "and a full cup \\[of wine\\]." WHERE id = 5705;
UPDATE quran_ayahs SET en_text = "They will not hear therein vain talk or lies –" WHERE id = 5706;
UPDATE quran_ayahs SET en_text = "a reward and a generous gift from your Lord," WHERE id = 5707;
UPDATE quran_ayahs SET en_text = "\\[from\\] the Lord of the heavens and earth and all that is between them, the Most Compassionate; none will dare to speak to Him." WHERE id = 5708;
UPDATE quran_ayahs SET en_text = "On the Day when the Spirit and the angels will stand in rows; none will dare to speak, except those to whom the Most Compassionate granted permission, and they will only speak the truth." WHERE id = 5709;
UPDATE quran_ayahs SET en_text = "That Day is sure to come. So whoever wills may seek a path leading to his Lord." WHERE id = 5710;
UPDATE quran_ayahs SET en_text = "Indeed, We have warned you of an imminent punishment on the Day when everyone will see what his hands have sent forth, and the disbeliever will say, “Oh, I wish that I were dust!”" WHERE id = 5711;
UPDATE quran_ayahs SET en_text = "By those who pull out \\[evil souls\\] harshly," WHERE id = 5712;
UPDATE quran_ayahs SET en_text = "and by those who draw out \\[good souls\\] gently," WHERE id = 5713;
UPDATE quran_ayahs SET en_text = "and by those who glide swiftly," WHERE id = 5714;
UPDATE quran_ayahs SET en_text = "and those who overtake one another as in a race," WHERE id = 5715;
UPDATE quran_ayahs SET en_text = "and those who carry out the command \\[of Allah\\]," WHERE id = 5716;
UPDATE quran_ayahs SET en_text = "on the Day when the earth convulses \\[by the first Blast\\]," WHERE id = 5717;
UPDATE quran_ayahs SET en_text = "followed by the second Blast," WHERE id = 5718;
UPDATE quran_ayahs SET en_text = "hearts will be pounding on that Day," WHERE id = 5719;
UPDATE quran_ayahs SET en_text = "and their eyes will be downcast." WHERE id = 5720;
UPDATE quran_ayahs SET en_text = "They say, “Will we be brought back to life," WHERE id = 5721;
UPDATE quran_ayahs SET en_text = "after we have turned into decayed bones?”" WHERE id = 5722;
UPDATE quran_ayahs SET en_text = "They say, “Then that would surely be a losing return!”" WHERE id = 5723;
UPDATE quran_ayahs SET en_text = "It will only be a single Blast," WHERE id = 5724;
UPDATE quran_ayahs SET en_text = "then they will immediately be above the ground." WHERE id = 5725;
UPDATE quran_ayahs SET en_text = "Has there come to you the story of Moses," WHERE id = 5726;
UPDATE quran_ayahs SET en_text = "when his Lord called out to him at the sacred valley of Tuwa?" WHERE id = 5727;
UPDATE quran_ayahs SET en_text = "\\[Saying,\\] “Go to Pharaoh, for he has transgressed all bounds”." WHERE id = 5728;
UPDATE quran_ayahs SET en_text = "And say: ‘Are you willing to be purified," WHERE id = 5729;
UPDATE quran_ayahs SET en_text = "and I will guide you to your Lord so that you may fear Him?’”" WHERE id = 5730;
UPDATE quran_ayahs SET en_text = "Then Moses showed him the greatest sign." WHERE id = 5731;
UPDATE quran_ayahs SET en_text = "But Pharaoh denied it and disobeyed." WHERE id = 5732;
UPDATE quran_ayahs SET en_text = "Then he turned back striving to plot." WHERE id = 5733;
UPDATE quran_ayahs SET en_text = "He gathered \\[his people\\] and proclaimed," WHERE id = 5734;
UPDATE quran_ayahs SET en_text = "saying, “I am your lord, the most high.”" WHERE id = 5735;
UPDATE quran_ayahs SET en_text = "So Allah seized him for an exemplary punishment in this life and in the Hereafter." WHERE id = 5736;
UPDATE quran_ayahs SET en_text = "Indeed, there is a lesson in this for those who fear Allah." WHERE id = 5737;
UPDATE quran_ayahs SET en_text = "Are you more difficult to create or the heaven that He built?" WHERE id = 5738;
UPDATE quran_ayahs SET en_text = "He raised it high and proportioned it," WHERE id = 5739;
UPDATE quran_ayahs SET en_text = "and darkened its night and brought forth its daylight." WHERE id = 5740;
UPDATE quran_ayahs SET en_text = "And thereafter He spread out the earth," WHERE id = 5741;
UPDATE quran_ayahs SET en_text = "brought forth from it its water and its pasture," WHERE id = 5742;
UPDATE quran_ayahs SET en_text = "and set the mountains firmly," WHERE id = 5743;
UPDATE quran_ayahs SET en_text = "as a provision for you and your grazing livestock." WHERE id = 5744;
UPDATE quran_ayahs SET en_text = "But when the Supreme Calamity comes," WHERE id = 5745;
UPDATE quran_ayahs SET en_text = "on that Day man will remember all what he did," WHERE id = 5746;
UPDATE quran_ayahs SET en_text = "and the Blazing Fire will be exposed for all to see." WHERE id = 5747;
UPDATE quran_ayahs SET en_text = "As for those who transgressed," WHERE id = 5748;
UPDATE quran_ayahs SET en_text = "and preferred the life of this world," WHERE id = 5749;
UPDATE quran_ayahs SET en_text = "the Blazing Fire will be their abode." WHERE id = 5750;
UPDATE quran_ayahs SET en_text = "But those who feared standing before their Lord and restrained themselves from evil desires," WHERE id = 5751;
UPDATE quran_ayahs SET en_text = "Paradise will surely be their abode." WHERE id = 5752;
UPDATE quran_ayahs SET en_text = "They ask you concerning the Hour, “When will it be?”" WHERE id = 5753;
UPDATE quran_ayahs SET en_text = "How could you possibly mention that?" WHERE id = 5754;
UPDATE quran_ayahs SET en_text = "Its knowledge rests only with your Lord." WHERE id = 5755;
UPDATE quran_ayahs SET en_text = "You are only a warner for those who fear it." WHERE id = 5756;
UPDATE quran_ayahs SET en_text = "On the Day when they see it, it will be as if they had remained \\[in this world\\] no more than an evening or a morning." WHERE id = 5757;
UPDATE quran_ayahs SET en_text = "He frowned and turned away," WHERE id = 5758;
UPDATE quran_ayahs SET en_text = "when the blind man came to him." WHERE id = 5759;
UPDATE quran_ayahs SET en_text = "How would you know? Perhaps he might be purified," WHERE id = 5760;
UPDATE quran_ayahs SET en_text = "or he might take heed and benefit from the reminder?" WHERE id = 5761;
UPDATE quran_ayahs SET en_text = "But he who was indifferent," WHERE id = 5762;
UPDATE quran_ayahs SET en_text = "you give him your full attention," WHERE id = 5763;
UPDATE quran_ayahs SET en_text = "although you are not to be blamed if he does not purify himself." WHERE id = 5764;
UPDATE quran_ayahs SET en_text = "But as for the one who came to you striving \\[for purification\\]," WHERE id = 5765;
UPDATE quran_ayahs SET en_text = "and he fears Allah," WHERE id = 5766;
UPDATE quran_ayahs SET en_text = "you let yourself to be distracted from him." WHERE id = 5767;
UPDATE quran_ayahs SET en_text = "No indeed; this is a reminder –" WHERE id = 5768;
UPDATE quran_ayahs SET en_text = "so whoever wills may give heed to it –" WHERE id = 5769;
UPDATE quran_ayahs SET en_text = "on venerable pages," WHERE id = 5770;
UPDATE quran_ayahs SET en_text = "exalted and purified," WHERE id = 5771;
UPDATE quran_ayahs SET en_text = "\\[preserved\\] in the hands of angel-scribes," WHERE id = 5772;
UPDATE quran_ayahs SET en_text = "honorable and obedient." WHERE id = 5773;
UPDATE quran_ayahs SET en_text = "Woe to man; how ungrateful he is!" WHERE id = 5774;
UPDATE quran_ayahs SET en_text = "From what did He create him?" WHERE id = 5775;
UPDATE quran_ayahs SET en_text = "He created him from a drop of semen and proportioned him," WHERE id = 5776;
UPDATE quran_ayahs SET en_text = "then He made the way easy for him;" WHERE id = 5777;
UPDATE quran_ayahs SET en_text = "then He caused him to die and be buried;" WHERE id = 5778;
UPDATE quran_ayahs SET en_text = "then when He wills, He will resurrect him." WHERE id = 5779;
UPDATE quran_ayahs SET en_text = "Yet he has not fulfilled what He commanded him." WHERE id = 5780;
UPDATE quran_ayahs SET en_text = "Let man consider the food he eats:" WHERE id = 5781;
UPDATE quran_ayahs SET en_text = "How We poured down rainwater in torrents," WHERE id = 5782;
UPDATE quran_ayahs SET en_text = "and caused the soil to split open \\[for sprouts\\]," WHERE id = 5783;
UPDATE quran_ayahs SET en_text = "and cause grains to grow in it," WHERE id = 5784;
UPDATE quran_ayahs SET en_text = "as well as grapes and fodder," WHERE id = 5785;
UPDATE quran_ayahs SET en_text = "and olive trees and date palms," WHERE id = 5786;
UPDATE quran_ayahs SET en_text = "and dense orchards," WHERE id = 5787;
UPDATE quran_ayahs SET en_text = "and fruits and grass," WHERE id = 5788;
UPDATE quran_ayahs SET en_text = "as provision for you and your livestock." WHERE id = 5789;
UPDATE quran_ayahs SET en_text = "But when the Deafening Blast comes," WHERE id = 5790;
UPDATE quran_ayahs SET en_text = "on that Day everyone will flee from his sibling," WHERE id = 5791;
UPDATE quran_ayahs SET en_text = "and from his mother and father," WHERE id = 5792;
UPDATE quran_ayahs SET en_text = "and from his wife and children." WHERE id = 5793;
UPDATE quran_ayahs SET en_text = "On that day, everyone will have enough concern of his own." WHERE id = 5794;
UPDATE quran_ayahs SET en_text = "On that Day, some faces will be bright," WHERE id = 5795;
UPDATE quran_ayahs SET en_text = "cheerful and rejoicing;" WHERE id = 5796;
UPDATE quran_ayahs SET en_text = "while other faces will be dust-stained," WHERE id = 5797;
UPDATE quran_ayahs SET en_text = "covered in darkness." WHERE id = 5798;
UPDATE quran_ayahs SET en_text = "Such are the disbelievers, the wicked." WHERE id = 5799;
UPDATE quran_ayahs SET en_text = "When the sun is wrapped up in darkness," WHERE id = 5800;
UPDATE quran_ayahs SET en_text = "and when the stars are scattered," WHERE id = 5801;
UPDATE quran_ayahs SET en_text = "and when the mountains are vanished," WHERE id = 5802;
UPDATE quran_ayahs SET en_text = "and when pregnant camels are left unattended," WHERE id = 5803;
UPDATE quran_ayahs SET en_text = "and when wild beasts are gathered," WHERE id = 5804;
UPDATE quran_ayahs SET en_text = "and when the seas are set on fire," WHERE id = 5805;
UPDATE quran_ayahs SET en_text = "and when the souls are sorted," WHERE id = 5806;
UPDATE quran_ayahs SET en_text = "and when the baby girl buried alive is asked," WHERE id = 5807;
UPDATE quran_ayahs SET en_text = "for what crime she was killed," WHERE id = 5808;
UPDATE quran_ayahs SET en_text = "and when records of deeds are spread open," WHERE id = 5809;
UPDATE quran_ayahs SET en_text = "and when the sky is stripped away," WHERE id = 5810;
UPDATE quran_ayahs SET en_text = "and when the Blazing Fire is flared up," WHERE id = 5811;
UPDATE quran_ayahs SET en_text = "and when Paradise is brought near –" WHERE id = 5812;
UPDATE quran_ayahs SET en_text = "then every soul will know what it has brought about." WHERE id = 5813;
UPDATE quran_ayahs SET en_text = "I swear by the receding stars," WHERE id = 5814;
UPDATE quran_ayahs SET en_text = "that rise and hide," WHERE id = 5815;
UPDATE quran_ayahs SET en_text = "and by the night as it departs," WHERE id = 5816;
UPDATE quran_ayahs SET en_text = "and by the day as it breaks –" WHERE id = 5817;
UPDATE quran_ayahs SET en_text = "Indeed, this \\[Qur’an\\] is a word conveyed by a noble angel-messenger." WHERE id = 5818;
UPDATE quran_ayahs SET en_text = "extremely powerful, highly revered with the Lord of the Throne," WHERE id = 5819;
UPDATE quran_ayahs SET en_text = "obeyed \\[by other angels\\] and moreover, trustworthy." WHERE id = 5820;
UPDATE quran_ayahs SET en_text = "Your fellow \\[the Prophet\\] is not madman." WHERE id = 5821;
UPDATE quran_ayahs SET en_text = "He indeed saw him \\[Gabriel\\] on the clear horizon." WHERE id = 5822;
UPDATE quran_ayahs SET en_text = "He does not withhold \\[knowledge of\\] the unseen." WHERE id = 5823;
UPDATE quran_ayahs SET en_text = "This \\[Qur’an\\] is not the word of an accursed devil." WHERE id = 5824;
UPDATE quran_ayahs SET en_text = "So which way are you going?" WHERE id = 5825;
UPDATE quran_ayahs SET en_text = "It is but a reminder to the worlds," WHERE id = 5826;
UPDATE quran_ayahs SET en_text = "for those among you who wish to take the straight path." WHERE id = 5827;
UPDATE quran_ayahs SET en_text = "But you cannot wish except by the Will of Allah, the Lord of the worlds." WHERE id = 5828;
UPDATE quran_ayahs SET en_text = "When the sky breaks apart." WHERE id = 5829;
UPDATE quran_ayahs SET en_text = "and when the stars fall, scattered," WHERE id = 5830;
UPDATE quran_ayahs SET en_text = "and when the seas burst forth," WHERE id = 5831;
UPDATE quran_ayahs SET en_text = "and when the graves are overturned," WHERE id = 5832;
UPDATE quran_ayahs SET en_text = "then each soul will come to know what it has done or what it has left undone." WHERE id = 5833;
UPDATE quran_ayahs SET en_text = "O mankind, what has lured you away from your Lord, the Most Generous," WHERE id = 5834;
UPDATE quran_ayahs SET en_text = "Who created you, then shaped and proportioned you," WHERE id = 5835;
UPDATE quran_ayahs SET en_text = "and assembled you in whatever form He willed?" WHERE id = 5836;
UPDATE quran_ayahs SET en_text = "No indeed; but you deny the Judgment Day," WHERE id = 5837;
UPDATE quran_ayahs SET en_text = "while there are watchers over you," WHERE id = 5838;
UPDATE quran_ayahs SET en_text = "honorable scribes," WHERE id = 5839;
UPDATE quran_ayahs SET en_text = "who know whatever you do." WHERE id = 5840;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will be in Bliss," WHERE id = 5841;
UPDATE quran_ayahs SET en_text = "and the wicked will be in the Blazing Fire," WHERE id = 5842;
UPDATE quran_ayahs SET en_text = "which they will enter on Judgment Day," WHERE id = 5843;
UPDATE quran_ayahs SET en_text = "and will never come out of it." WHERE id = 5844;
UPDATE quran_ayahs SET en_text = "How do you know what Judgment Day is?" WHERE id = 5845;
UPDATE quran_ayahs SET en_text = "Again, how do you know what Judgment Day is?" WHERE id = 5846;
UPDATE quran_ayahs SET en_text = "It is the Day when no soul will be of any help to another, and all command on that Day will be with Allah." WHERE id = 5847;
UPDATE quran_ayahs SET en_text = "Woe to the defrauders," WHERE id = 5848;
UPDATE quran_ayahs SET en_text = "those who take full measure when they take from others," WHERE id = 5849;
UPDATE quran_ayahs SET en_text = "but they give less when they measure or weigh for them." WHERE id = 5850;
UPDATE quran_ayahs SET en_text = "Do they really not think that they will be resurrected" WHERE id = 5851;
UPDATE quran_ayahs SET en_text = "for a momentous Day," WHERE id = 5852;
UPDATE quran_ayahs SET en_text = "a Day when people will stand before the Lord of the worlds?" WHERE id = 5853;
UPDATE quran_ayahs SET en_text = "No indeed! The record of the wicked is surely in Sijjīn." WHERE id = 5854;
UPDATE quran_ayahs SET en_text = "How do you know what Sijjīn is?" WHERE id = 5855;
UPDATE quran_ayahs SET en_text = "It is a clearly inscribed Record." WHERE id = 5856;
UPDATE quran_ayahs SET en_text = "Woe on that Day to the deniers," WHERE id = 5857;
UPDATE quran_ayahs SET en_text = "those who deny the Judgment Day!" WHERE id = 5858;
UPDATE quran_ayahs SET en_text = "None denies it except every sinful transgressor." WHERE id = 5859;
UPDATE quran_ayahs SET en_text = "When Our verses are recited to him, he says, “Ancient fables!”" WHERE id = 5860;
UPDATE quran_ayahs SET en_text = "No indeed! Their hearts have been stained by what they used to commit!" WHERE id = 5861;
UPDATE quran_ayahs SET en_text = "No indeed! On that Day they will be barred from seeing their Lord." WHERE id = 5862;
UPDATE quran_ayahs SET en_text = "They will then surely enter the Blazing Fire," WHERE id = 5863;
UPDATE quran_ayahs SET en_text = "then they will be told, “This is what you used to deny.”" WHERE id = 5864;
UPDATE quran_ayahs SET en_text = "No indeed! The record of the righteous is certainly in ‘illiyyīn –" WHERE id = 5865;
UPDATE quran_ayahs SET en_text = "and how do you know what ‘illiyyūn is? –" WHERE id = 5866;
UPDATE quran_ayahs SET en_text = "It is a record inscribed," WHERE id = 5867;
UPDATE quran_ayahs SET en_text = "witnessed by those who are close to Allah." WHERE id = 5868;
UPDATE quran_ayahs SET en_text = "Indeed, the righteous will be in Bliss," WHERE id = 5869;
UPDATE quran_ayahs SET en_text = "seated on the couches, gazing in wonder." WHERE id = 5870;
UPDATE quran_ayahs SET en_text = "You will see on their faces the radiance of bliss." WHERE id = 5871;
UPDATE quran_ayahs SET en_text = "They will be served to drink pure wine from a sealed container," WHERE id = 5872;
UPDATE quran_ayahs SET en_text = "whose last sip is musk – let those who strive, strive for that –" WHERE id = 5873;
UPDATE quran_ayahs SET en_text = "and it will be mixed with water from the spring of Tasnīm," WHERE id = 5874;
UPDATE quran_ayahs SET en_text = "a spring from which those who are close to Allah will drink." WHERE id = 5875;
UPDATE quran_ayahs SET en_text = "Indeed, the wicked used to laugh at the believers;" WHERE id = 5876;
UPDATE quran_ayahs SET en_text = "when they passed by them, they would wink at one another," WHERE id = 5877;
UPDATE quran_ayahs SET en_text = "and when they went back to their people, they would return exulting." WHERE id = 5878;
UPDATE quran_ayahs SET en_text = "When they saw the believers, they would say, “These people are truly misguided,”" WHERE id = 5879;
UPDATE quran_ayahs SET en_text = "although they were not sent as their keepers." WHERE id = 5880;
UPDATE quran_ayahs SET en_text = "So Today the believers will laugh at the disbelievers," WHERE id = 5881;
UPDATE quran_ayahs SET en_text = "while sitting on the couches, gazing around." WHERE id = 5882;
UPDATE quran_ayahs SET en_text = "Have the disbelievers been rewarded except for what they used to do?" WHERE id = 5883;
UPDATE quran_ayahs SET en_text = "When the sky is rent asunder," WHERE id = 5884;
UPDATE quran_ayahs SET en_text = "and hearkens to its Lord, as it must," WHERE id = 5885;
UPDATE quran_ayahs SET en_text = "and when the earth is leveled out," WHERE id = 5886;
UPDATE quran_ayahs SET en_text = "and casts out all what it contains, and becomes empty," WHERE id = 5887;
UPDATE quran_ayahs SET en_text = "and hearkens to its Lord, as it must," WHERE id = 5888;
UPDATE quran_ayahs SET en_text = "O humans, you are toiling towards your Lord, and you will surely meet Him." WHERE id = 5889;
UPDATE quran_ayahs SET en_text = "Whoever is given his Record in his right hand," WHERE id = 5890;
UPDATE quran_ayahs SET en_text = "he will have an easy reckoning," WHERE id = 5891;
UPDATE quran_ayahs SET en_text = "and he will return to his people joyfully." WHERE id = 5892;
UPDATE quran_ayahs SET en_text = "But whoever is given his Record from behind his back," WHERE id = 5893;
UPDATE quran_ayahs SET en_text = "he will call out for destruction," WHERE id = 5894;
UPDATE quran_ayahs SET en_text = "and he will enter the Blazing Fire." WHERE id = 5895;
UPDATE quran_ayahs SET en_text = "Indeed, he used to live among his people joyfully." WHERE id = 5896;
UPDATE quran_ayahs SET en_text = "and he thought that he would never return \\[to Allah\\]." WHERE id = 5897;
UPDATE quran_ayahs SET en_text = "Indeed he will! His Lord was always watching him." WHERE id = 5898;
UPDATE quran_ayahs SET en_text = "Indeed, I swear by the twilight," WHERE id = 5899;
UPDATE quran_ayahs SET en_text = "and by the night and what it covers," WHERE id = 5900;
UPDATE quran_ayahs SET en_text = "and by the moon when it becomes full," WHERE id = 5901;
UPDATE quran_ayahs SET en_text = "You will surely pass from one stage to another." WHERE id = 5902;
UPDATE quran_ayahs SET en_text = "Then what is the matter with them that they do not believe," WHERE id = 5903;
UPDATE quran_ayahs SET en_text = "and when the Qur’an is recited to them, they do not prostrate?" WHERE id = 5904;
UPDATE quran_ayahs SET en_text = "Rather, the disbelievers persist in denying it," WHERE id = 5905;
UPDATE quran_ayahs SET en_text = "but Allah knows best whatever they harbor in their hearts." WHERE id = 5906;
UPDATE quran_ayahs SET en_text = "So give them the tidings of a painful punishment." WHERE id = 5907;
UPDATE quran_ayahs SET en_text = "But those who believe and do righteous deeds will have a never-ending reward." WHERE id = 5908;
UPDATE quran_ayahs SET en_text = "By the sky full of constellations," WHERE id = 5909;
UPDATE quran_ayahs SET en_text = "and by the promised Day," WHERE id = 5910;
UPDATE quran_ayahs SET en_text = "and by the witness and the witnessed," WHERE id = 5911;
UPDATE quran_ayahs SET en_text = "cursed be the makers of the trench," WHERE id = 5912;
UPDATE quran_ayahs SET en_text = "the fire full of fuel," WHERE id = 5913;
UPDATE quran_ayahs SET en_text = "when they sat around it," WHERE id = 5914;
UPDATE quran_ayahs SET en_text = "and were witnessing what they did to the believers." WHERE id = 5915;
UPDATE quran_ayahs SET en_text = "Their only grievance against them was that they believed in Allah, the All-Mighty, the Praiseworthy," WHERE id = 5916;
UPDATE quran_ayahs SET en_text = "to Whom belongs the dominion of the heavens and earth, and Allah is Witness over all things." WHERE id = 5917;
UPDATE quran_ayahs SET en_text = "Indeed, for those who tortured the believing men and women, then did not repent, there will be the punishment of Hell and the punishment of the burning fire." WHERE id = 5918;
UPDATE quran_ayahs SET en_text = "But those who believed and did righteous deeds will certainly have gardens under which rivers flow; that is the supreme triumph." WHERE id = 5919;
UPDATE quran_ayahs SET en_text = "Indeed, the punishment of your Lord is severe." WHERE id = 5920;
UPDATE quran_ayahs SET en_text = "Indeed, it is He Who originates \\[the creation\\] then repeats it," WHERE id = 5921;
UPDATE quran_ayahs SET en_text = "and He is the All-Forgiving, the Most Affectionate," WHERE id = 5922;
UPDATE quran_ayahs SET en_text = "the Lord of the Glorious Throne;" WHERE id = 5923;
UPDATE quran_ayahs SET en_text = "He does whatever He wills." WHERE id = 5924;
UPDATE quran_ayahs SET en_text = "Has there not come to you the story of the forces" WHERE id = 5925;
UPDATE quran_ayahs SET en_text = "of Pharaoh and Thamūd?" WHERE id = 5926;
UPDATE quran_ayahs SET en_text = "But the disbelievers are in persistent denial," WHERE id = 5927;
UPDATE quran_ayahs SET en_text = "although Allah encompasses them from all sides." WHERE id = 5928;
UPDATE quran_ayahs SET en_text = "But this is a glorious Qur’an," WHERE id = 5929;
UPDATE quran_ayahs SET en_text = "\\[inscribed\\] in a Preserved Tablet." WHERE id = 5930;
UPDATE quran_ayahs SET en_text = "By the sky and the night comer –" WHERE id = 5931;
UPDATE quran_ayahs SET en_text = "and how do you know what the night comer is?" WHERE id = 5932;
UPDATE quran_ayahs SET en_text = "It is the star of piercing brightness –" WHERE id = 5933;
UPDATE quran_ayahs SET en_text = "there is no soul except that there is a watcher over it." WHERE id = 5934;
UPDATE quran_ayahs SET en_text = "Let man reflect on what he was created from." WHERE id = 5935;
UPDATE quran_ayahs SET en_text = "He was created from a spurting fluid," WHERE id = 5936;
UPDATE quran_ayahs SET en_text = "emerging from between the backbone and the ribs." WHERE id = 5937;
UPDATE quran_ayahs SET en_text = "Indeed, Allah is able to bring him back to life." WHERE id = 5938;
UPDATE quran_ayahs SET en_text = "On the Day when all secrets will be disclosed," WHERE id = 5939;
UPDATE quran_ayahs SET en_text = "he will then have no power, nor any helper." WHERE id = 5940;
UPDATE quran_ayahs SET en_text = "By the sky and its recurring rain," WHERE id = 5941;
UPDATE quran_ayahs SET en_text = "and by the earth that cracks open \\[with the sprout of plants\\]," WHERE id = 5942;
UPDATE quran_ayahs SET en_text = "Indeed, this \\[Qur’an\\] is a decisive word," WHERE id = 5943;
UPDATE quran_ayahs SET en_text = "which is not for amusement." WHERE id = 5944;
UPDATE quran_ayahs SET en_text = "Indeed, they are devising a plan." WHERE id = 5945;
UPDATE quran_ayahs SET en_text = "I am too devising a plan." WHERE id = 5946;
UPDATE quran_ayahs SET en_text = "So bear with the disbelievers, and give them respite for a while." WHERE id = 5947;
UPDATE quran_ayahs SET en_text = "Glorify the name of your Lord, the Most High," WHERE id = 5948;
UPDATE quran_ayahs SET en_text = "Who created and fashioned in due proportion," WHERE id = 5949;
UPDATE quran_ayahs SET en_text = "and Who determined \\[the creation\\] then guided them," WHERE id = 5950;
UPDATE quran_ayahs SET en_text = "and Who brings forth the pasture," WHERE id = 5951;
UPDATE quran_ayahs SET en_text = "then makes it into withered chaff." WHERE id = 5952;
UPDATE quran_ayahs SET en_text = "We will teach you \\[the Qur’an\\], so you will not forget," WHERE id = 5953;
UPDATE quran_ayahs SET en_text = "except what Allah wills, for indeed He knows what is manifest and what is hidden." WHERE id = 5954;
UPDATE quran_ayahs SET en_text = "We will guide you to the easy way." WHERE id = 5955;
UPDATE quran_ayahs SET en_text = "So remind, if the reminder should benefit." WHERE id = 5956;
UPDATE quran_ayahs SET en_text = "He who fears Allah will take heed," WHERE id = 5957;
UPDATE quran_ayahs SET en_text = "but the wretched will avoid it," WHERE id = 5958;
UPDATE quran_ayahs SET en_text = "who will enter the Great Fire," WHERE id = 5959;
UPDATE quran_ayahs SET en_text = "wherein he will neither die nor live." WHERE id = 5960;
UPDATE quran_ayahs SET en_text = "Indeed, he who purifies himself will attain success," WHERE id = 5961;
UPDATE quran_ayahs SET en_text = "and remembers his Lord’s Name and prays." WHERE id = 5962;
UPDATE quran_ayahs SET en_text = "But you prefer the life of this world," WHERE id = 5963;
