UPDATE quran_ayahs SET en_text = "the Scriptures of Abraham and Moses." WHERE id = 5966;
UPDATE quran_ayahs SET en_text = "Has there come to you the story of the Overwhelming Event?" WHERE id = 5967;
UPDATE quran_ayahs SET en_text = "On that Day, some faces will be humiliated," WHERE id = 5968;
UPDATE quran_ayahs SET en_text = "toiling and weary," WHERE id = 5969;
