UPDATE quran_ayahs SET en_text = "Other faces on that Day will be joyful," WHERE id = 5974;
UPDATE quran_ayahs SET en_text = "well-pleased with their past striving," WHERE id = 5975;
UPDATE quran_ayahs SET en_text = "in an elevated garden," WHERE id = 5976;
UPDATE quran_ayahs SET en_text = "wherein no idle talk will be heard." WHERE id = 5977;
UPDATE quran_ayahs SET en_text = "There will be a flowing spring;" WHERE id = 5978;
UPDATE quran_ayahs SET en_text = "and couches raised high," WHERE id = 5979;
UPDATE quran_ayahs SET en_text = "and cups placed ready," WHERE id = 5980;
UPDATE quran_ayahs SET en_text = "and cushions lined up," WHERE id = 5981;
UPDATE quran_ayahs SET en_text = "and splendid carpets spread out." WHERE id = 5982;
UPDATE quran_ayahs SET en_text = "Do they not reflect on the camel, how it is created;" WHERE id = 5983;
UPDATE quran_ayahs SET en_text = "and the sky, how it is raised high;" WHERE id = 5984;
UPDATE quran_ayahs SET en_text = "and the mountains, how they are firmly set up;" WHERE id = 5985;
UPDATE quran_ayahs SET en_text = "and the earth, how it is spread out?" WHERE id = 5986;
UPDATE quran_ayahs SET en_text = "So remind \\[O Prophet\\]; your duty is only to remind;" WHERE id = 5987;
UPDATE quran_ayahs SET en_text = "you are not assigned to compel them." WHERE id = 5988;
UPDATE quran_ayahs SET en_text = "But whoever turns away and disbelieves," WHERE id = 5989;
UPDATE quran_ayahs SET en_text = "Allah will inflict upon him the greatest punishment." WHERE id = 5990;
UPDATE quran_ayahs SET en_text = "Indeed, to Us is their return," WHERE id = 5991;
UPDATE quran_ayahs SET en_text = "then it is for Us to call them to account." WHERE id = 5992;
UPDATE quran_ayahs SET en_text = "By the dawn," WHERE id = 5993;
UPDATE quran_ayahs SET en_text = "and the ten nights," WHERE id = 5994;
UPDATE quran_ayahs SET en_text = "and the even and the odd," WHERE id = 5995;
UPDATE quran_ayahs SET en_text = "and by the night when it comes," WHERE id = 5996;
UPDATE quran_ayahs SET en_text = "are these oaths not sufficient for those who have sense?" WHERE id = 5997;
UPDATE quran_ayahs SET en_text = "Have you not considered how your Lord dealt with ‘Ād" WHERE id = 5998;
UPDATE quran_ayahs SET en_text = "of Iram, who were people of great stature," WHERE id = 5999;
UPDATE quran_ayahs SET en_text = "the like of whom had never been created in the land;" WHERE id = 6000;
UPDATE quran_ayahs SET en_text = "and Thamūd, who carved out the rocks in the Valley;" WHERE id = 6001;
UPDATE quran_ayahs SET en_text = "and the mighty and powerful Pharaoh?" WHERE id = 6002;
UPDATE quran_ayahs SET en_text = "All of them transgressed in the land," WHERE id = 6003;
UPDATE quran_ayahs SET en_text = "and spread therein much corruption," WHERE id = 6004;
UPDATE quran_ayahs SET en_text = "so your Lord poured upon them a scourge of punishment." WHERE id = 6005;
UPDATE quran_ayahs SET en_text = "Indeed, your Lord is ever vigilant." WHERE id = 6006;
UPDATE quran_ayahs SET en_text = "As for man, when he is tested by his Lord by honoring him and bestowing blessings upon him, he says, “My Lord has \\[deservedly\\] honored me!”" WHERE id = 6007;
UPDATE quran_ayahs SET en_text = "But when he is tested by restricting his provision, he says, “My Lord has \\[undeservedly\\] humiliated me!”" WHERE id = 6008;
UPDATE quran_ayahs SET en_text = "No indeed! You are not kind to the orphan," WHERE id = 6009;
UPDATE quran_ayahs SET en_text = "nor do you urge one another to feed the needy," WHERE id = 6010;
UPDATE quran_ayahs SET en_text = "and you greedily consume the entire inheritance," WHERE id = 6011;
UPDATE quran_ayahs SET en_text = "and love wealth immensely." WHERE id = 6012;
UPDATE quran_ayahs SET en_text = "No indeed! When the earth is crushed over and over," WHERE id = 6013;
UPDATE quran_ayahs SET en_text = "and your Lord comes with the angels, rank after rank," WHERE id = 6014;
UPDATE quran_ayahs SET en_text = "and Hell is brought near on that Day – this is when man will understand, but what is the use of that understanding?" WHERE id = 6015;
UPDATE quran_ayahs SET en_text = "He will say, “Would that I had sent forth something for my \\[eternal\\] life!”" WHERE id = 6016;
UPDATE quran_ayahs SET en_text = "None can punish as He punishes on that Day," WHERE id = 6017;
UPDATE quran_ayahs SET en_text = "and none can bind as He binds." WHERE id = 6018;
UPDATE quran_ayahs SET en_text = "\\[It will be said to the believer,\\] “O the tranquil soul!" WHERE id = 6019;
UPDATE quran_ayahs SET en_text = "Return to your Lord, well pleased and pleasing \\[to Him\\]." WHERE id = 6020;
UPDATE quran_ayahs SET en_text = "Join My \\[righteous\\] slaves," WHERE id = 6021;
UPDATE quran_ayahs SET en_text = "and enter My Paradise.”" WHERE id = 6022;
UPDATE quran_ayahs SET en_text = "I do swear by this city \\[of Makkah\\] –" WHERE id = 6023;
UPDATE quran_ayahs SET en_text = "and you are free of restriction \\[for a while\\] in this city –" WHERE id = 6024;
UPDATE quran_ayahs SET en_text = "and by every parent and offspring," WHERE id = 6025;
UPDATE quran_ayahs SET en_text = "We have created man in hardship." WHERE id = 6026;
UPDATE quran_ayahs SET en_text = "Does he think that none can ever have power over him?" WHERE id = 6027;
UPDATE quran_ayahs SET en_text = "He says, “I have squandered enormous wealth!”" WHERE id = 6028;
UPDATE quran_ayahs SET en_text = "Does he think that no one sees him?" WHERE id = 6029;
UPDATE quran_ayahs SET en_text = "Have We not given him two eyes," WHERE id = 6030;
UPDATE quran_ayahs SET en_text = "and a tongue and two lips," WHERE id = 6031;
UPDATE quran_ayahs SET en_text = "and shown him the two ways \\[of right and wrong\\]?" WHERE id = 6032;
UPDATE quran_ayahs SET en_text = "Yet he did not make his way through the steep path," WHERE id = 6033;
UPDATE quran_ayahs SET en_text = "and how do you know what the steep path is?" WHERE id = 6034;
UPDATE quran_ayahs SET en_text = "It is freeing a slave," WHERE id = 6035;
UPDATE quran_ayahs SET en_text = "or giving food on a day of hunger," WHERE id = 6036;
UPDATE quran_ayahs SET en_text = "to an orphan relative," WHERE id = 6037;
UPDATE quran_ayahs SET en_text = "or to a needy person in misery," WHERE id = 6038;
UPDATE quran_ayahs SET en_text = "then \\[most importantly\\] to be one of those who believe and exhort one another to patience and exhort one another to compassion." WHERE id = 6039;
UPDATE quran_ayahs SET en_text = "Such are the people of the Right." WHERE id = 6040;
UPDATE quran_ayahs SET en_text = "As for those who reject Our signs, they are the people of the Left," WHERE id = 6041;
UPDATE quran_ayahs SET en_text = "who will be locked up in the Fire." WHERE id = 6042;
UPDATE quran_ayahs SET en_text = "By the sun and its brightness," WHERE id = 6043;
UPDATE quran_ayahs SET en_text = "and by the moon as it follows it," WHERE id = 6044;
UPDATE quran_ayahs SET en_text = "and by the day as it displays it," WHERE id = 6045;
UPDATE quran_ayahs SET en_text = "and by the night as it covers it," WHERE id = 6046;
UPDATE quran_ayahs SET en_text = "and by the sky and how He made it," WHERE id = 6047;
UPDATE quran_ayahs SET en_text = "and by the earth and how He spread it," WHERE id = 6048;
UPDATE quran_ayahs SET en_text = "and by the soul and how He fashioned it," WHERE id = 6049;
UPDATE quran_ayahs SET en_text = "then inspired it \\[to know\\] its wickedness and righteousness," WHERE id = 6050;
UPDATE quran_ayahs SET en_text = "Indeed, he who purifies his soul will attain success," WHERE id = 6051;
UPDATE quran_ayahs SET en_text = "and he who corrupts it will be doomed." WHERE id = 6052;
UPDATE quran_ayahs SET en_text = "Thamūd rejected \\[the messenger\\] because of their transgression," WHERE id = 6053;
UPDATE quran_ayahs SET en_text = "when the most wretched among them rose \\[to kill the camel\\]." WHERE id = 6054;
UPDATE quran_ayahs SET en_text = "Although the messenger of Allah warned them, “Hands off the she-camel of Allah and her drink!”" WHERE id = 6055;
UPDATE quran_ayahs SET en_text = "Yet they rejected him and hamstrung her. So their Lord destroyed them for their sin and leveled them to the ground." WHERE id = 6056;
UPDATE quran_ayahs SET en_text = "And He has no fear of the consequences thereof." WHERE id = 6057;
UPDATE quran_ayahs SET en_text = "By the night when it covers," WHERE id = 6058;
UPDATE quran_ayahs SET en_text = "and by the day when it appears," WHERE id = 6059;
UPDATE quran_ayahs SET en_text = "and by His creation of male and female," WHERE id = 6060;
UPDATE quran_ayahs SET en_text = "surely your efforts are divergent." WHERE id = 6061;
UPDATE quran_ayahs SET en_text = "As for the one who gives in charity and fears Allah," WHERE id = 6062;
UPDATE quran_ayahs SET en_text = "and truly believes in the best reward," WHERE id = 6063;
UPDATE quran_ayahs SET en_text = "We will make easy for him the way of salvation." WHERE id = 6064;
UPDATE quran_ayahs SET en_text = "But as for the one who is miserly and deems himself self-sufficient," WHERE id = 6065;
UPDATE quran_ayahs SET en_text = "and denies the best reward," WHERE id = 6066;
UPDATE quran_ayahs SET en_text = "We will make easy for him the way of perdition." WHERE id = 6067;
UPDATE quran_ayahs SET en_text = "His wealth will be of no avail to him when he tumbles \\[into Hell\\]." WHERE id = 6068;
UPDATE quran_ayahs SET en_text = "Indeed, it is upon Us to show the right way," WHERE id = 6069;
UPDATE quran_ayahs SET en_text = "and surely to Us belong the Hereafter and the present life." WHERE id = 6070;
UPDATE quran_ayahs SET en_text = "So I warn you of a raging Fire," WHERE id = 6071;
UPDATE quran_ayahs SET en_text = "none will enter it except the most wretched," WHERE id = 6072;
UPDATE quran_ayahs SET en_text = "who denied and turned away." WHERE id = 6073;
UPDATE quran_ayahs SET en_text = "But the most righteous will be saved from it," WHERE id = 6074;
UPDATE quran_ayahs SET en_text = "who spends his wealth to purify himself," WHERE id = 6075;
UPDATE quran_ayahs SET en_text = "not in return for someone’s favors," WHERE id = 6076;
UPDATE quran_ayahs SET en_text = "but to seek the pleasure of his Lord, the Most High." WHERE id = 6077;
UPDATE quran_ayahs SET en_text = "He will surely be well pleased." WHERE id = 6078;
UPDATE quran_ayahs SET en_text = "By the morning brightness," WHERE id = 6079;
UPDATE quran_ayahs SET en_text = "and by the night when it is still," WHERE id = 6080;
UPDATE quran_ayahs SET en_text = "your Lord has not forsaken you \\[O Prophet\\], nor does He hate you;" WHERE id = 6081;
UPDATE quran_ayahs SET en_text = "the Hereafter is better for you than the present life;" WHERE id = 6082;
UPDATE quran_ayahs SET en_text = "and your Lord will certainly give you so much that you will be well pleased." WHERE id = 6083;
