UPDATE quran_ayahs SET en_text = "Did He not find you unguided then guide you?" WHERE id = 6085;
UPDATE quran_ayahs SET en_text = "Did He not find you in need then enrich you?" WHERE id = 6086;
UPDATE quran_ayahs SET en_text = "So do not mistreat the orphan," WHERE id = 6087;
UPDATE quran_ayahs SET en_text = "nor repulse the beggar;" WHERE id = 6088;
UPDATE quran_ayahs SET en_text = "and proclaim the blessings of your Lord." WHERE id = 6089;
UPDATE quran_ayahs SET en_text = "Have We not reassured your heart for you \\[O Prophet\\]," WHERE id = 6090;
UPDATE quran_ayahs SET en_text = "and relieved you of your burden" WHERE id = 6091;
UPDATE quran_ayahs SET en_text = "that weighed down your back," WHERE id = 6092;
UPDATE quran_ayahs SET en_text = "and raised high your fame for you?" WHERE id = 6093;
UPDATE quran_ayahs SET en_text = "For indeed, with hardship comes ease," WHERE id = 6094;
UPDATE quran_ayahs SET en_text = "indeed, with hardship comes ease." WHERE id = 6095;
UPDATE quran_ayahs SET en_text = "So whenever you are free, strive in worship," WHERE id = 6096;
UPDATE quran_ayahs SET en_text = "and turn to your Lord in devotion." WHERE id = 6097;
UPDATE quran_ayahs SET en_text = "By the fig and the olive \\[of Jerusalem\\]," WHERE id = 6098;
UPDATE quran_ayahs SET en_text = "and by Mount Sinai \\[of Egypt\\]," WHERE id = 6099;
UPDATE quran_ayahs SET en_text = "and by this secure city \\[of Makkah\\]," WHERE id = 6100;
UPDATE quran_ayahs SET en_text = "Surely We have created man in the best form," WHERE id = 6101;
