UPDATE quran_ayahs SET en_text = "except those who believe and do righteous deeds; they will have a never-ending reward." WHERE id = 6103;
UPDATE quran_ayahs SET en_text = "Then, what makes you deny the Judgment Day?" WHERE id = 6104;
