UPDATE quran_ayahs SET en_text = "Read, and your Lord is the Most Generous," WHERE id = 6108;
UPDATE quran_ayahs SET en_text = "Who taught by the pen," WHERE id = 6109;
UPDATE quran_ayahs SET en_text = "taught man what he did not know." WHERE id = 6110;
UPDATE quran_ayahs SET en_text = "Indeed, man transgresses" WHERE id = 6111;
