UPDATE quran_ayahs SET en_text = "Indeed, to your Lord is the return." WHERE id = 6113;
UPDATE quran_ayahs SET en_text = "Have you seen the one who prevents" WHERE id = 6114;
UPDATE quran_ayahs SET en_text = "a slave \\[of Allah\\] from praying?" WHERE id = 6115;
