UPDATE quran_ayahs SET en_text = "We will call the wardens of Hellfire." WHERE id = 6123;
UPDATE quran_ayahs SET en_text = "No indeed; do not obey him, but prostrate and draw near \\[to Allah\\]." WHERE id = 6124;
UPDATE quran_ayahs SET en_text = "Indeed, We sent this \\[Qur’an\\] down on the Night of Decree." WHERE id = 6125;
UPDATE quran_ayahs SET en_text = "How do you know what the Night of Decree is?" WHERE id = 6126;
UPDATE quran_ayahs SET en_text = "The Night of Decree is better than a thousand months." WHERE id = 6127;
UPDATE quran_ayahs SET en_text = "On that night the angels and the Spirit \\[Gabriel\\] descend by their Lord’s permission with all decrees." WHERE id = 6128;
UPDATE quran_ayahs SET en_text = "It is all peace until the break of dawn." WHERE id = 6129;
UPDATE quran_ayahs SET en_text = "Those who disbelieve from the People of the Book and the polytheists were not going to desist \\[from disbelief\\] until there came to them clear proof," WHERE id = 6130;
UPDATE quran_ayahs SET en_text = "a messenger from Allah, reciting pure pages" WHERE id = 6131;
UPDATE quran_ayahs SET en_text = "containing upright commandments." WHERE id = 6132;
UPDATE quran_ayahs SET en_text = "Yet those who were given the Scripture did not disagree except after the clear proof came to them," WHERE id = 6133;
UPDATE quran_ayahs SET en_text = "although they were commanded only to worship Allah with sincere devotion to Him, being inclined to true faith, and to establish prayer and give zakah; that is the true religion." WHERE id = 6134;
UPDATE quran_ayahs SET en_text = "Those who disbelieve from the People of the Book and the polytheists will be in the fire of Hell, abiding therein forever. It is they who are the worst of creatures." WHERE id = 6135;
UPDATE quran_ayahs SET en_text = "As for those who believe and do righteous deeds – it is they who are the best of creatures." WHERE id = 6136;
UPDATE quran_ayahs SET en_text = "Their reward with their Lord is Gardens of Eternity under which rivers flow, abiding therein forever. Allah is pleased with them and they are pleased with Him. This is for those who fear their Lord." WHERE id = 6137;
UPDATE quran_ayahs SET en_text = "When the earth is shaken with a mighty quake," WHERE id = 6138;
UPDATE quran_ayahs SET en_text = "and the earth throws out its burdens," WHERE id = 6139;
UPDATE quran_ayahs SET en_text = "and man says, “What is the matter with it?”" WHERE id = 6140;
UPDATE quran_ayahs SET en_text = "On that Day it will recount all its news," WHERE id = 6141;
UPDATE quran_ayahs SET en_text = "because your Lord has inspired it \\[to do so\\]." WHERE id = 6142;
UPDATE quran_ayahs SET en_text = "On that Day all people will come forward in separate groups to be shown their deeds." WHERE id = 6143;
UPDATE quran_ayahs SET en_text = "So whoever does an atom’s weight of good will see it," WHERE id = 6144;
UPDATE quran_ayahs SET en_text = "and whoever does an atom’s weight of evil will see it." WHERE id = 6145;
UPDATE quran_ayahs SET en_text = "By the galloping, panting horses," WHERE id = 6146;
UPDATE quran_ayahs SET en_text = "striking sparks of fire \\[with their hooves\\]," WHERE id = 6147;
UPDATE quran_ayahs SET en_text = "launching raids at dawn," WHERE id = 6148;
UPDATE quran_ayahs SET en_text = "stirring up thereby clouds of dust," WHERE id = 6149;
UPDATE quran_ayahs SET en_text = "plunging thereby into the midst of the enemy," WHERE id = 6150;
UPDATE quran_ayahs SET en_text = "indeed, man is very ungrateful to his Lord –" WHERE id = 6151;
UPDATE quran_ayahs SET en_text = "and he himself is a witness to that –" WHERE id = 6152;
UPDATE quran_ayahs SET en_text = "and he is truly extreme in his love of wealth." WHERE id = 6153;
UPDATE quran_ayahs SET en_text = "Does he not know that the contents of graves will be overturned," WHERE id = 6154;
UPDATE quran_ayahs SET en_text = "and the secrets of the hearts will be brought to light?" WHERE id = 6155;
UPDATE quran_ayahs SET en_text = "Indeed, their Lord is All-Aware of them on that Day." WHERE id = 6156;
UPDATE quran_ayahs SET en_text = "The Striking Calamity!" WHERE id = 6157;
UPDATE quran_ayahs SET en_text = "What is the Striking Calamity?" WHERE id = 6158;
UPDATE quran_ayahs SET en_text = "How do you know what the Striking Calamity is?" WHERE id = 6159;
UPDATE quran_ayahs SET en_text = "On that Day people will be like scattered moths," WHERE id = 6160;
UPDATE quran_ayahs SET en_text = "and the mountains will be like carded wool." WHERE id = 6161;
UPDATE quran_ayahs SET en_text = "Then the one whose scales of good deeds are heavy," WHERE id = 6162;
UPDATE quran_ayahs SET en_text = "will have a pleasant life." WHERE id = 6163;
UPDATE quran_ayahs SET en_text = "But the one whose scales of good deeds are light," WHERE id = 6164;
UPDATE quran_ayahs SET en_text = "his abode will be the abyss." WHERE id = 6165;
UPDATE quran_ayahs SET en_text = "And how do you know what it is?" WHERE id = 6166;
UPDATE quran_ayahs SET en_text = "It is a Blazing Fire." WHERE id = 6167;
UPDATE quran_ayahs SET en_text = "Competition for worldly gains distracts you \\[from Allah\\]," WHERE id = 6168;
UPDATE quran_ayahs SET en_text = "until you come to your graves." WHERE id = 6169;
UPDATE quran_ayahs SET en_text = "No indeed! You will come to know." WHERE id = 6170;
UPDATE quran_ayahs SET en_text = "Again no! You will come to know." WHERE id = 6171;
UPDATE quran_ayahs SET en_text = "No indeed! If only you knew for certain." WHERE id = 6172;
UPDATE quran_ayahs SET en_text = "You will surely see the Blazing Fire," WHERE id = 6173;
UPDATE quran_ayahs SET en_text = "Again, you will see it with absolute certainty." WHERE id = 6174;
UPDATE quran_ayahs SET en_text = "Then on that Day you will surely be asked about your worldly pleasures." WHERE id = 6175;
UPDATE quran_ayahs SET en_text = "By the time," WHERE id = 6176;
UPDATE quran_ayahs SET en_text = "man is in utter loss," WHERE id = 6177;
UPDATE quran_ayahs SET en_text = "except those who believe and do righteous deeds, and exhort one another to the truth and exhort one another to patience." WHERE id = 6178;
UPDATE quran_ayahs SET en_text = "Woe to every backbiter and slanderer," WHERE id = 6179;
