UPDATE quran_ayahs SET en_text = "and how do you know what the Crushing Fire is?" WHERE id = 6183;
UPDATE quran_ayahs SET en_text = "It is the kindled Fire of Allah," WHERE id = 6184;
UPDATE quran_ayahs SET en_text = "which reaches the hearts." WHERE id = 6185;
UPDATE quran_ayahs SET en_text = "It will be closed down upon them," WHERE id = 6186;
UPDATE quran_ayahs SET en_text = "locked with towering columns." WHERE id = 6187;
UPDATE quran_ayahs SET en_text = "Have you not seen how your Lord dealt with the people of the Elephant?" WHERE id = 6188;
