UPDATE quran_ayahs SET en_text = "pelting them with stones of baked clay," WHERE id = 6191;
UPDATE quran_ayahs SET en_text = "leaving them like chewed up and trampled chaff." WHERE id = 6192;
UPDATE quran_ayahs SET en_text = "For the accustomed security of the Quraysh," WHERE id = 6193;
UPDATE quran_ayahs SET en_text = "secure in their winter and summer journeys." WHERE id = 6194;
UPDATE quran_ayahs SET en_text = "Let them worship the Lord of this \\[Sacred\\] House," WHERE id = 6195;
UPDATE quran_ayahs SET en_text = "Who fed them against hunger and made them secure against fear." WHERE id = 6196;
UPDATE quran_ayahs SET en_text = "Have you seen the one who denies the Recompense?" WHERE id = 6197;
