UPDATE quran_ayahs SET en_text = "and does not urge others to feed the needy." WHERE id = 6199;
UPDATE quran_ayahs SET en_text = "So woe to those who pray," WHERE id = 6200;
UPDATE quran_ayahs SET en_text = "but are heedless of their prayer;" WHERE id = 6201;
UPDATE quran_ayahs SET en_text = "those who only show off," WHERE id = 6202;
UPDATE quran_ayahs SET en_text = "and withhold even the small kindnesses." WHERE id = 6203;
UPDATE quran_ayahs SET en_text = "We have surely given you \\[O Prophet\\] Al-Kauthar \\[abundance\\]," WHERE id = 6204;
UPDATE quran_ayahs SET en_text = "so pray and offer sacrifice to your Lord alone." WHERE id = 6205;
UPDATE quran_ayahs SET en_text = "Indeed, the one who hates you is truly cut off \\[from all goodness\\]." WHERE id = 6206;
UPDATE quran_ayahs SET en_text = "Say, “O disbelievers," WHERE id = 6207;
UPDATE quran_ayahs SET en_text = "I do not worship what you worship," WHERE id = 6208;
UPDATE quran_ayahs SET en_text = "nor do you worship what I worship." WHERE id = 6209;
UPDATE quran_ayahs SET en_text = "Never will I worship what you worship," WHERE id = 6210;
